/// <summary>
///   Edit project.  
/// </summary>
/// <param name = "_ctrlPage">
///   An object for caller class.  
/// </param>
void editProject(Object _ctrlPage)
{
    container           cGeneral,
                        cAddress;
    container           sortingId   = [SortingGroup_sortingId_1.text(), SortingGroup_sortingId_2.text(), SortingGroup_sortingId_3.text()];
    DimensionDefault    defaultDimension = projTableSortingAndDimension.DefaultDimension;

    cGeneral   = [projTableInsert_ProjGroupId.text(),projTableInsert_Status.selection(),projTableInsert_ResponsibleSalesWorker.value(),projTableInsert_ResponsibleWorker.value(),
                  projTableInsert_ResponsibleFinWorker.value(),projTableInsert_ProjectedStartDate.dateValue(),projTableInsert_StartDate.dateValue(),
                  projTableInsert_ProjectedEndDate.dateValue(),projTableInsert_ExtensionDate.dateValue(),projTableInsert_EndDate.dateValue(),
                  projTableInsert_TaxGroupId.text(),projTableInsert_PriceGroup.text()];

    cGeneral += this.addExtendedProjTableValues();

    cAddress   = [addressGroup1_copyAddress.value(), contact_Email.text()];

    if (newProjIdEdit && oldProjId.text() && [oldProjId.text(), parentProjIdEdit, newProjIdEdit, newProjIdEdit, projCreateNameMethod.selection(), projTableCreateName.text(), invoiceprojId.text(),cGeneral,cAddress] != projTableGenerateValues)
    {
        projTableGenerateValues = [oldProjId.text(), parentProjIdEdit, newProjIdEdit, newProjType, projCreateNameMethod.selection(),
                                  projTableCreateName.text(), invoiceprojId.text(), invoiceAccount.text(), cGeneral, cAddress, sortingId, defaultDimension];

        _ctrlPage.createProject(projTableGenerateValues);
        oldIdArray = _ctrlPage.setOldIdArray();
        newIdArray = _ctrlPage.setNewIdArray();
        projCount  = _ctrlPage.setProjCount();
    }
    else
    {
        if (!oldProjId.text())
        {
            checkFailed("@SYS30751");
        }

        if (!newProjIdEdit)
        {
            checkFailed(strFmt("@SYS26332",newProjId.label()));
        }
    }
}

void endUpdate(boolean updateDB = false)
{
    int     countProj = 0;

    #macrolib.AviFiles

    int                  countTotal,countCur,idx,tmpIdx,dbCreated;
    SysOperationProgress sysOperationProgress;
    Object               ctrlSteps;
    boolean              setupExists;
    FormListItem         fli;

    void load(boolean   setParm = false)
    {
        if (setParm)
        {
            ctrlSteps.parmOldId(oldProjId.text());
            ctrlSteps.parmNewId(newProjIdEdit);
        }

        if (updateDB)
        {
            sysOperationProgress.setText(RunBase::getDescription(classidget(ctrlSteps)));
            if (sysWizard.numberSeq())
            {
                sysWizard.numberSeq().used();
            }
            ctrlSteps.insertDB();
            sysOperationProgress.incCount();
            return;
        }

        countCur    = ctrlSteps.createdQtyFile();
        stepsCount  = countCur ? stepsCount+1 : stepsCount;
        countTotal += countCur;

        if (countCur)
        {
            dbCreated = ctrlSteps.createdQtyDB();
            if (!setupExists && dbCreated)
            {
                setupExists = true;
            }

            tmpIdx++;
            fli = new FormListItem(RunBase::getDescription(classidget(ctrlSteps)), 1);
            fli.idx(tmpIdx);
            idx = listview.addItem(fli);
            listview.setText(idx,int2str(countCur),1);
            listview.setText(idx,int2str(dbCreated),2);
        }
    }
    ;
    listview.deleteAll();

    if (!updateDB)
    {
        stepsCount = 0;
    }

    if (updateDB)
    {
        if (!stepsCount)
        {
            return;
        }

        sysOperationProgress = new SysOperationProgress();
        sysOperationProgress.setCaption("@SYS58629");
        sysOperationProgress.setTotal(stepsCount);
    }

    /* Load ProjTable */
    if (newProjIdEdit)
    {
        ctrlSteps = new ProjTableWizardCtrl(sysWizard,projTableInsert_DS);
        countProj = ctrlSteps.createdQtyFile();

        // Send the template projectId.
        if (isConfigurationkeyEnabled(configurationKeyNum(Project)))
        {
            ctrlSteps.parmCopyPwpSetting(copyPwpSetting.value());
            ctrlSteps.parmCopyVendRetention(copyVendRetentionTerms.value());
        }
        ctrlSteps.templateProjId(templateProjId);
        ctrlSteps.parmNewId(newProjIdEdit);
        load();
    }

    /* Load ProjActivity */
    if (createActivity.value())
    {
        ctrlSteps = ProjTableWizardActivityCtrl::construct();
        ctrlSteps.parmProjTable(projTableInsert_DS);
        load(true);
    }

    /* Load ProjWIPTable */
    if (countProj)
    {
        ctrlSteps = ProjTableWizardEstimatesCtrl::construct();
        ctrlSteps.parmProjTable(projTableInsert_DS);
        ctrlSteps.parmProjCount(projCount);
        ctrlSteps.parmOldProjArray(oldIdArray);
        ctrlSteps.parmNewProjArray(newIdArray);
        load(true);
    }

    /* Load ProjSalesPrice */
    if (createSalesPrice.value())
    {
        ctrlSteps = ProjTableWizardSalesPriceCtrl::construct();
        ctrlSteps.parmProjTable(projTableInsert_DS);
        load(true);
    }

    /* Load ProjCostPrice */
    if (createCostPrice.value())
    {
        ctrlSteps = ProjTableWizardCostPriceCtrl::construct();
        ctrlSteps.parmProjTable(projTableInsert_DS);
        load(true);
    }

    /* Load ProjSalesPrice cost*/
    if (createSalesPriceCost.value())
    {
        ctrlSteps = ProjTableWizardSalesPriceCostCtrl::construct();
        ctrlSteps.parmProjTable(projTableInsert_DS);
        load(true);
    }

    /* Load ProjSalesPrice cost*/
    if (createRevenuePrice.value())
    {
        ctrlSteps = ProjTableWizardRevenuePriceCtrl::construct();
        ctrlSteps.parmProjTable(projTableInsert_DS);
        load(true);
    }

    /* Load SMASubscriptionPrice */
    if (createSubscriptionPrice.value())
    {
        ctrlSteps = ProjTableWizardSubscriptionPriceCtrl::construct();
        ctrlSteps.parmProjTable(projTableInsert_DS);
        load(true);
    }

    /* Load ProjDefaultOffsetSetup */
    if (createDefaultOffsetAccount.value())
    {
        ctrlSteps = ProjTableWizardDefaultOffsetAccountCtrl::construct();
        ctrlSteps.parmProjTable(projTableInsert_DS);
        load(true);
    }

    /* Load validation of category */
    if (createValCategory.value())
    {
        ctrlSteps = ProjTableWizardValCategoryCtrl::construct();
        ctrlSteps.parmProjTable(projTableInsert_DS);
        load(true);
    }

    /* Load validation of employees */
    if (createValEmployee.value())
    {
        ctrlSteps = ProjTableWizardValEmployeeCtrl::construct();
        ctrlSteps.parmProjTable(projTableInsert_DS);
        load(true);
    }

    /* Lineproperty */
    if (createLineproperty.value())
    {
        ctrlSteps = ProjTableWizardLinepropertyCtrl::construct();
        ctrlSteps.parmProjTable(projTableInsert_DS);
        load(true);
    }

    // Ledger posting
    if (createLedgerPosting.value())
    {
        ctrlSteps = ProjTableWizardProjPostingCtrl::construct();
        ctrlSteps.parmProjTable(projTableInsert_DS);
        load(true);
    }

    if (updateDB)
    {
        return;
    }

    if (!countTotal)
    {
        endTxt.text("@SYS55970");
    }
    else
    {
        endTxt.text(strFmt("@SYS58630",countTotal));
    }
}

    void init()
    {
        ProjTable           checkPreviousProject;

        super();

		if (element.args().caller())
        {   
			sysWizard = element.args().caller();

			projTableWizardCtrl = new ProjTableWizardCtrl(sysWizard, projTableInsert_ds);
			oldIdArray = new Array(Types::String);
			newIdArray = new Array(Types::String);

			/* setup page 1 */
			bullit_1.text("@SYS58741");
			bullit_15.text("@SYS59538");
			bullit_2.text("@SYS58555");
			bullit_3.text("@SYS53423");
			bullit_4.text("@SYS58866");
			bullit_5.text("@SYS58868");
			bullit_6.text("@SYS58863");
			bullit_7.text("@SYS58871");

			/* setup last page */
			listview.addColumn(1,new FormListColumn("@SYS7576",1,130));
			listview.addColumn(2,new FormListColumn("@SYS55971",2,65));
			listview.addColumn(3,new FormListColumn("@SYS55972",2,75));

			this.initSetup();

			xSysLastValue::getLast(this);

			checkPreviousProject = ProjTable::find(templateProjId);

			if (sysWizard.getArgs().caller() && sysWizard.getArgs().caller().name() == formStr(SMAAgreementWizard))
			{
				if (checkPreviousProject.Template && checkPreviousProject.Type != ProjType::Time)
				{
					oldProjId.text(templateProjId);
					this.initFromProject(templateProjId);
				}
			}
			else
			{
				if (checkPreviousProject.Template)
				{
					oldProjId.text(templateProjId);
					this.initFromProject(templateProjId);
				}
			}

			projCreateNameMethod.selection(wizardProjName);
        }
        else
		{
            ProjTableWizard::main(new Args());
			element.closeCancel();
		}

        //Set focus on the first tab
        bullit_1.setFocus();
    }

void initFromProject(ProjId _projId)
{
    ProjTable               projTable = ProjTable::find(_projId);
    LogisticsPostalAddress  postalAddress = LogisticsPostalAddress::findByLocation(projTable.DeliveryLocation);

    if (projTable)
    {
        addressGroup1_DlvName.text(postalAddress.displayLocationDescription());
        addressGroup1_DlvAddress.text(postalAddress.Address);
        contact_Email.text(projTable.Email);
        projTableInsert_EndDate.dateValue(projTable.EndDate);
        projTableInsert_ProjGroupId.text(projTable.ProjGroupId);
        projTableInsert_ResponsibleWorker.value(projTable.WorkerResponsible);
        projTableInsert_ResponsibleFinWorker.value(projTable.WorkerResponsibleFinancial);
        projTableInsert_ResponsibleSalesWorker.value(projTable.WorkerResponsibleSales);
        projTableInsert_ProjectedStartDate.dateValue(projTable.ProjectedStartDate);
        projTableInsert_StartDate.dateValue(projTable.StartDate);
        projTableInsert_ProjectedEndDate.dateValue(projTable.ProjectedEndDate);
        projTableInsert_ExtensionDate.dateValue(projTable.ExtensionDate);
        projTableInsert_Status.selection(enum2int(projTable.Status));
        projTableInsert_TaxGroupId.text(projTable.TaxGroupId);
        projTableInsert_PriceGroup.text(projTable.ProjPriceGroup);
        projTableSortingAndDimension.SortingId = projTable.SortingId;
        projTableSortingAndDimension.DefaultDimension = projTable.DefaultDimension;
        projTableSortingAndDimension.ProjId = projTable.ProjId;
        projTableSortingAndDimension.ProjGroupId=projTable.ProjGroupId;
        projTableSortingAndDimension.ProjInvoiceProjId=projTable.ProjInvoiceProjId;
        projTableSortingAndDimension.Type = ProjMultipleContractLinesForProjectFeatureHelper::isFeatureEnabled() ? ProjType::None : ProjGroup::find(projTable.ProjGroupId).ProjType;
        projTableSortingAndDimension.Status = ProjStatusTypeRule::firstStage(projTableSortingAndDimension.Type);
        projTableSortingAndDimension.doUpdate();
        projTableInsert.CheckBudget = projTable.CheckBudget;

        // Add price group
        if (isConfigurationkeyEnabled(configurationKeyNum(Project)))
        {
            projTableInsert_PriceGroup.text(projTable.ProjPriceGroup);
        }
    }
}

void initPage(int fromPage, int toPage)
{
    Object              ctrlPage = element.projWizardTableCtrl(toPage);
    ProjTable           projTable;
    ProjTable           projAssociation;
    boolean             foundActivities = false;

    switch (fromPage)
    {
        case #PROJECT :
            if (toPage > fromPage)
            {
                this.leaveSetupPage();

                if (!projTableInsert_ProjGroupId.text())
                {
                    newProjType = ProjTable::find(oldProjId.text()).Type;
                }
                else
                {
                    newProjType = ProjGroup::find(projTableInsert_ProjGroupId.text()).ProjType;
                }
            }
            break;
    }

    switch (toPage)
    {
        case #PROJECT     :
            if (!oldProjid.text())
            {
                oldProjId.text((select projTable where projTable.Template == NoYes::Yes).ProjId);
                this.initFromProject(oldProjid.text());
            }

            if (!ProjTable::isNumberSeqManual() && !sysWizard.numberSeq())
            {
                sysWizard.initNumberSeq();

                if (sysWizard.numberSeq() && !newProjIdEdit)
                {
                    this.setNewProjId(sysWizard.numberSeq().num());
                }
            }

            if (ProjTable::isAllowChangeUp() || ProjTable::isAllowChangeDown())
            {
                newProjId.enabled(true);
            }
            else
            {
                newProjId.enabled(ProjTable::isNumberSeqManual());
            }

            if (!oldProjId)
            {
                projTableProjIdSuggested = true;
                oldProjId.text(ctrlPage.templateProjid());
                this.initFromProject(oldProjid.text());
            }

            if (projCreateNameMethod.selection() == WizardProjName::TemplateProject)
            {
                projTableCreateName.enabled(false);
            }
            else
            {
                projTableCreateName.enabled(true);
            }

            validProjID = oldProjId.text();
            if (!projTableInsert_ProjGroupId.text())
            {
                newProjType = ProjTable::find(oldProjId.text()).Type;
            }

            break;

        case #GENERAL     :
            if (oldProjId)
            {
                // Set Contract Id and Customer Account number if not set already
                if (!invoiceProjId.text())
                {
                    invoiceProjId.text(ProjTable::find(oldProjid.text()).ProjInvoiceProjId);
                }

                if (!invoiceAccount.text())
                {
                    invoiceAccount.text(ProjFundingSource::findCustAccount(invoiceProjId.text()).CustAccount);
                }
            }

            element.setInvoice();
            break;

        case #RELATION    :

            element.setInvoice();

            cmdButtonSelectAll.setFocus();

            // PRICES
            if ((select projRevenueSalesPrice where projRevenueSalesPrice.ProjId like oldProjId.text() + '*').RecId)
            {
                createSalesPriceCost.enabled(true);
                createRevenuePrice.enabled(true);
                createSalesPriceCost.setFocus();
            }
            else
            {
                createRevenuePrice.value(0);
                createRevenuePrice.enabled(false);
            }

            if ((select projCostSalesPrice where projCostSalesPrice.ProjId like oldProjId.text() + '*').RecId)
            {
                createSalesPriceCost.enabled(true);
                createSalesPriceCost.setFocus();
            }
            else
            {
                createSalesPriceCost.value(0);
                createSalesPriceCost.enabled(false);
            }

            if ((select projHourCostPrice where projHourCostPrice.ProjId like oldProjId.text() + '*').RecId)
            {
                createCostPrice.enabled(true);
                createCostPrice.setFocus();
            }
            else
            {
                createCostPrice.value(0);
                createCostPrice.enabled(false);
            }

            if ((select projHourSalesPrice where projHourSalesPrice.ProjId like oldProjId.text() + '*').RecId)
            {
                createSalesPrice.enabled(true);
                createSalesPrice.setFocus();
            }
            else
            {
                createSalesPrice.value(0);
                createSalesPrice.enabled(false);
            }

            if ((select smaSalesPriceSubscription where smaSalesPriceSubscription.ProjId like oldProjId.text() + '*').RecId)
            {
                createSubscriptionPrice.enabled(true);
            }
            else
            {
                createSubscriptionPrice.value(0);
                createSubscriptionPrice.enabled(false);
            }

            if ((select projDefaultOffsetSetup where projDefaultOffsetSetup.ProjId like oldProjId.text() + '*').RecId)
            {
                createDefaultOffsetAccount.enabled(true);
            }
            else
            {
                createDefaultOffsetAccount.value(0);
                createDefaultOffsetAccount.enabled(false);
            }

            // VALIDATION
            if ((select projValEmplProjSetUp where projValEmplProjSetUp.Projid like oldProjId.text() + '*').RecId)
            {
                createValEmployee.enabled(true);
                createValEmployee.setFocus();
            }
            else
            {
                createValEmployee.value(0);
                createValEmployee.enabled(false);
            }

            if ((select projValProjCategorySetUp where projValProjCategorySetUp.ProjId like oldProjId.text() + '*').RecId)
            {
                createValCategory.enabled(true);
                createValCategory.setFocus();
            }
            else
            {
                createValCategory.value(0);
                createValCategory.enabled(false);
            }

            // PROJECT
            if ((select projPosting where projPosting.ProjRelation like oldProjId.text() + '*').RecId)
            {
                createLedgerPosting.enabled(true);
                createLedgerPosting.setFocus();
            }
            else
            {
                createLedgerPosting.value(0);
                createLedgerPosting.enabled(false);
            }

            if ((select projLinePropertySetup where projLinePropertySetup.ProjRelation like oldProjId.text() + '*').RecId)
            {
                createLineproperty.enabled(true);
                createLineproperty.setFocus();
            }
            else
            {
                createLineproperty.value(0);
                createLineproperty.enabled(false);
            }

            while select projAssociation where projAssociation.ProjId like oldProjId.text() + '*'
            {
                if  (HierarchyTreeTable::associationLeafExist(projAssociation, ElementNodeType::Activity) ||
                     HierarchyTreeTable::associationLeafExist(projAssociation, ElementNodeType::Node))
                {
                    foundActivities = true;
                    break;
                }
            }

            if  (foundActivities)
            {
                createActivity.enabled(true);
                createActivity.setFocus();
            }
            else
            {
                createActivity.value(0);
                createActivity.enabled(false);
            }
            break;

        case #EDITPROJECT :
            element.editProject(ctrlPage);
            break;

        case #LASTPAGE    :
            element.endUpdate(false);
    }

    element.setNext();
}

