public void setCustAccount(CustAccount _custAccount)
{
    invoiceAccount.text(_custAccount);
}

void setInvoice()
{
    switch (newProjType)
    {
        case ProjType::Time:
            invoiceProjId.text('');
            invoiceProjId.enabled(false);

            invoiceAccount.text('');
            invoiceAccount.enabled(false);
            break;

        case ProjType::Cost, ProjType::Internal, ProjType::Investment       :
            invoiceProjId.text('');
            invoiceProjId.enabled(false);
            invoiceAccount.enabled(true);
            break;

        default:
            invoiceProjId.enabled(true);
            invoiceAccount.enabled(true);
            break;
    }

    validInvoiceAccount = invoiceAccount.text();

    element.setNext();
}

void setNewProjId(ProjId projId)
{
    newProjIdEdit         = projId;
}

void setNext()
{
    boolean  ok = true;

    switch (sysWizard.curTab())
    {
        case #PROJECT :
            ok  = (oldProjId.text() && newProjIdEdit && !ProjTable::exist(newProjIdEdit)) ? true : false;
            break;

        case #GENERAL :
            if (projTableWizardCtrl.validateDateFields(projTableInsert_StartDate.dateValue(),projTableInsert_EndDate.dateValue(),
                                                       projTableInsert_ProjectedStartDate.dateValue(),projTableInsert_ProjectedEndDate.dateValue(),
                                                       projTableInsert_ExtensionDate.dateValue()))
            {
                switch (newProjType)
                {
                    case ProjType::Cost       :
                    case ProjType::Internal   :
                    case ProjType::Investment :
                    case ProjType::Time       :
                        ok  = true;
                        break;

                    default:
                        ok  = ((invoiceProjid.text()) && invoiceAccount.validate()) ? true : false;
                        break;
                }
            }
            else
            {
                ok = false;
                sysWizard.nextEnabled(false);
            }
            break;
    }

    sysWizard.nextEnabled(ok, sysWizard.curTab(), false);
}

