boolean childExist(ProjTable _projTable)
{
    ProjTable   projChild;
    ProjId      projId = _projTable.ProjId;

    projChild.setTmp();
    projChild.setTmpData(_projTable);

    select projChild where projChild.ParentId == projId;

    if (projChild)
    {
        return true;
    }

    return false;
}

