boolean checkOldProjId()
{
    boolean ret = true;

    if (this.text())
    {
        if (! ProjTable::find(this.text()).Template)
        {
            ret = checkFailed(strFmt("@SYS58805",this.text()));
            this.text(validProjID);
        }
    }

    return ret;
}

