    }
}

boolean modified()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        ret = this.checkOldProjId();
    }

    validProjID = oldProjId.text();
    if (!projTableInsert_ProjGroupId.text())
    {
        newProjType = ProjTable::find(oldProjId.text()).Type;
    }
    else
    {
        projTableInsert_ProjGroupId.text('');
        newProjType = ProjTable::find(oldProjId.text()).Type;
        validProjGroupId = '';
    }

    element.setInvoice();
    element.initFromProject(validProjID);

    return ret;
}

boolean validate()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        ret = this.checkOldProjId();

        if (sysWizard.getArgs().caller() && sysWizard.getArgs().caller().name() == formStr(SMAAgreementWizard))
        {
            // Transfer the project id of the new project back to the agreement wizard
            if (ProjTable::find(this.text()).Type == ProjType::Time)
            {
                // Time projects are now allowed on service agreements
                ret = checkFailed("@SYS106660");
            }
        }
    }

    return ret;
}

