public boolean modified()
{
    boolean                 ret;
    Integer                 sonNum;
    ProjId                  projFormat;
    ProjId                  projIdLastSon;
    NumberSeqFormHandler    numberSeqFormHandler;

    #DEFINE.SymbolNumerals('#')

    ret = super();

    numberSeqFormHandler = NumberSeqFormHandler::newForm(ProjParameters::numRefProjId().NumberSequenceId,
                                                             element,
                                                             projTableInsert_DS,
                                                             fieldNum(ProjTable,ProjId)
                                                            );

    if (ProjTable::find(this.text()).Format)
    {
        projIdLastSon           = ProjTable::projIdLastSon(parentProjIdEdit);
        if (projIdLastSon)
        {
            projFormat          = subStr(projIdLastSon,
                                            strLen(parentProjIdEdit)+1,
                                            (strLen(projIdLastSon)-strLen(parentProjIdEdit)+1));

            sonNum              = NumberSeq::numRemoveFormat(projFormat, ProjTable::find(parentProjIdEdit).Format);
        }
        else
        {
            sonNum = 0;
        }

        element.setNewProjId(parentProjIdEdit + NumberSeq::numInsertFormat(sonNum+1,ProjTable::find(parentProjIdEdit).Format));
    }

    if (!ProjTable::isNumberSeqManual() && sysWizard.numberSeq() && !parentProjIdEdit)
    {
        element.setNewProjId(sysWizard.numberSeq().num());
    }

        newProjId.validate();

    element.setNext();

    return ret;
}

public boolean validate()
{
    boolean ret;

    ret = super();

    if (this.text() && !ProjTable::find(this.text()))
    {
        warning(strFmt("@SYS55265", this.text(),
                                 this.label(),
                                 tableId2pname(tableNum(ProjTable))));

        return false;
    }

    if  (this.text() && !ProjTable::find(this.text()).Format)
    {
        ret=checkFailed(strFmt("@SYS67673",this.text()));
    }

    return ret;
}

