public boolean modified()
{
    ProjTable   projTable;
    ProjId      tmpProjId;
    boolean     ret;

    ret = super();

    if (ret)
    {
        //
        // Need to include subprojects of template
        // when validating ProjGroup assessement principal of NoWIp
        //
        if (ProjRevRecHelper::getCompletePrincipleForProject(validProjID, false, true) == ProjCompletePrincip::NoWIP)
        {
            tmpProjId = validProjID + '*';
            select firstonly * from projTable where projTable.ProjId like tmpProjId
                && projTable.wipProject;
            if (projTable)
            {
                warning("@SYS107959");
                this.text('');
                ret = false;
            }
        }

        validProjGroupId = this.text();
        if (validProjGroupId)
        {
            newProjType = ProjGroup::find(this.text()).ProjType;
        }
        else
        {
            newProjType = ProjTable::find(oldProjId.text()).Type;
        }

        element.setInvoice();
    }

    return ret;
}

public boolean validate()
{
    boolean ret;

    ret = super();

    if (ret && this.text() && !ProjGroup::find(this.text()))
    {
        warning(strFmt("@SYS55265", this.text(),
                                 fieldPName(ProjTable, ProjGroupId),
                                 tablePName(ProjGroup)));

        this.text(validProjGroupId);

        return false;
    }

    if (ret && this.text() )
    {
        // ProjGroup found - validate the assessement principal
        ProjTable projTableLocal = ProjTable::find(validProjID);

        if (projTableLocal.wipProject)
        {
            if (ProjRevRecHelper::getCompletePrincipleForProjTable(projTableLocal) == ProjCompletePrincip::NoWIP)
            {
                warning("@SYS107959");
                this.text('');
                ret = false;
            }
        }
    }

    newProjType = ProjGroup::find(this.text()).ProjType;

    return ret;
}

