    ProjValGroupServer::addEmplCategoryGroupToResource(data, resourceRecId);
}

void buildQuery()
{
    str                         groupStr;
    ProjValEmplCategorySetUp    setupTbl;

    // Build a filtering condition based on all the groupID associated to the Employee in ProjValEmplCategorySetup table
    if (resourceRecId)
    {
        while select crossCompany GroupId
            from setupTbl
            group by GroupId
            where setupTbl.Resource == resourceRecId &&
                setupTbl.GroupId
        {
            if (!groupStr)
            {
                groupStr = strFmt(
                        '((%1.%2 == "%3") && (%1.%4 == 0))',
                        qbd.name(),
                        fieldStr(ProjValEmplCategorySetUp, GroupId),
                        queryValue(setupTbl.GroupId),
                        fieldStr(ProjValEmplCategorySetUp, Resource));
            }
            else
            {
                groupStr += strFmt(
                        ' || ((%1.%2 == "%3") && (%1.%4 == 0))',
                        qbd.name(),
                        fieldStr(ProjValEmplCategorySetUp, GroupId),
                        queryValue(setupTbl.GroupId),
                        fieldStr(ProjValEmplCategorySetUp, Resource));
            }
        }

        if (!groupStr)
        {
            groupStr = strFmt(
                    '((%1.%2 == %3) && (%1.%4 == ""))',
                    qbd.name(),
                    fieldStr(ProjValEmplCategorySetUp, Resource),
                    queryValue(resourceRecId),
                    fieldStr(ProjValEmplCategorySetUp, GroupId));
        }
        else
        {
            groupStr += strFmt(
                    '|| ((%1.%2 == %3) && (%1.%4 == ""))',
                    qbd.name(),
                    fieldStr(ProjValEmplCategorySetUp, Resource),
                    queryValue(resourceRecId),
                    fieldStr(ProjValEmplCategorySetUp, GroupId));
        }
    }

    if (groupStr)
    {
        list_GroupId.value(groupStr);
    }
    else
    {
        list_GroupId.value(SysQuery::valueEmptyString());
    }
}

