public void init()
{
    if (element.args().dataset() == tableNum(HcmWorker))
    {
        HcmWorker hcmWorker = element.args().record();
        resourceRecId = ResourceFacade::findByWorker(hcmWorker.RecId);

        ResourceSetup resourceSetup;

        select firstonly RecId from resourceSetup
            where resourceSetup.Resource == resourceRecId;

        if (!resourceSetup.RecId)
        {
            throw error("@Proj:ProjectSetUpNotCompleted");
        }
    }

    super();

    if ((element.args().dataset() != tableNum(ProjCategory)) && (element.args().dataset() != tableNum(HcmWorker)) && (element.args().dataset() != tablenum(ResourceView)))
    {
        element.args().record(null);
    }
}

public void run()
{
    super();
}

public void init()
{
    Query   q   = new Query();
    QueryBuildDataSource qbds;            
    List                 companyList = new List(Types::String);
    ListEnumerator       listEnumerator;

    super();

    projCategory_ds.autoQuery(false);
    qbds = q.addDataSource(tableNum(ProjCategory));

    companyList = LedgerInterCompany::findAllDestinationCompanies();

    if (companyList.elements() > 0)
    {
        q.allowCrossCompany(true);

        companyList.addEnd(curext());                
        listEnumerator = companyList.getEnumerator();
        while (listEnumerator.moveNext())
        {
            q.addCompanyRange(listEnumerator.current());
        }
    }

    this.query(q);
}

public void linkActive()
{
    HcmWorker       hcmWorker;
    ResourceView    resourceView;

    resourceRecId  = 0;

    if (element.args().dataset() == tableNum(HcmWorker))
    {
        hcmWorker       = element.args().record();
        resourceRecId   = ResourceFacade::findByWorker(hcmWorker.RecId);
        element.buildQuery();
    }
    else if (element.args().dataset() == tablenum(ResourceView))
    {
        resourceView = element.args().record();
        resourceRecId = resourceView.RecId;
        element.buildQuery();
    }

    super();
}

public void init()
{
    Query                   q;
    QueryBuildDataSource    qbr1;
    QueryBuildDataSource    qbr2;

    q = new Query();

    super();

    // Build query to get all the groups currently associated with the current employee
    qbr1 = q.addDataSource(tableNum(ProjValEmplCategoryTable), 'ProjValEmplCategoryTable_group');
    qbr2 = qbr1.addDataSource(tableNum(ProjValEmplCategorySetUp));

    qbr2.relations(true);

    qbr2.addSortField(fieldNum(ProjValEmplCategorySetUp, GroupId));
    qbr2.addRange(fieldNum(ProjValEmplCategorySetUp, GroupId)).value(SysQuery::valueNotEmptyString());

    group_EmplId    = qbr2.addRange(fieldNum(ProjValEmplCategorySetUp, Resource));

    q.allowCrossCompany(true);

    this.query(q);
}

public void linkActive()
{
    HcmWorker   hcmWorker;
