public void init()
{
    Query                q;
    QueryBuildDataSource qbr1;
    QueryBuildDataSource qbr2;

    q = new Query();

    super();

    // Build query to get all the categories currently NOT associated with the current employee
    qbr1 = q.addDataSource(tableNum(ProjCategory));
    qbr1.addRange(fieldNum(ProjCategory, CategoryType)).value(queryValue(ProjCategoryType::Hour));
    qbr1.addRange(fieldNum(ProjCategory, CategoryType)).value(queryValue(ProjCategoryType::Cost));
    qbr1.addRange(fieldNum(ProjCategory, CategoryType)).value(queryValue(ProjCategoryType::Revenue));

    qbr2 = qbr1.addDataSource(tableNum(ProjValEmplCategorySetUp));

    qbr2.relations(true);
    qbr2.joinMode(JoinMode::NoExistsJoin);
    qbr2.addSortField(fieldNum(ProjValEmplCategorySetUp, GroupId));
    qbr2.addRange(fieldNum(ProjValEmplCategorySetUp, GroupId)).value(SysQuery::valueEmptyString());

    notElement_EmplId = qbr2.addRange(fieldNum(ProjValEmplCategorySetUp, Resource));

    this.query(q);
}

