[Form]
    public class ProjValSetupCategoryProj extends FormRun
    {
        [FormObservable] ProjId              projId;

        QueryBuildRange     group_ProjId;
        QueryBuildRange     notGroup_ProjId;
        QueryBuildRange     element_ProjId;
        QueryBuildRange     notElement_ProjId;
        QueryBuildRange     list_ProjId;
        QueryBuildRange     list_GroupId;
        QueryBuildDataSource        qbd;

        //Define validation on project invoice table
        ProjInvoiceProjId   m_sProjInvoiceProjId;
}

void addCategoryToProj()
{
    ProjCategory    locProjCategory;
    container       data;

    locProjCategory = projCategory_NotEmpl_DS.getFirst(1);

    if (!locProjCategory)
    {
        data += projCategory_NotEmpl.CategoryId;
    }
    else
    {
        while (locProjCategory)
        {
            data += locProjCategory.CategoryId;
            locProjCategory = projCategory_NotEmpl_DS.getNext();
        }
    }
     ProjValElementServer::addCategoryToProj(data, projId);
}

void addGroupToProj()
{
    ProjValProjCategoryTable    locProjValProjCategoryTable;
    container                   data;

    locProjValProjCategoryTable = projValProjCategoryTable_NotGroup_DS.getFirst(1);

    if (!locProjValProjCategoryTable)
    {
        data    += projValProjCategoryTable_NotGroup.GroupId;
    }
    else
    {
        while (locProjValProjCategoryTable)
        {
            data += locProjValProjCategoryTable.GroupId;
            locProjValProjCategoryTable = projValProjCategoryTable_NotGroup_DS.getNext();
        }
    }
    ProjValGroupServer::addProjCategoryGroupToProj(data, projId);

}

void buildQuery()
{
    str     groupStr;
    ProjValProjCategorySetUp    setupTbl;

    if (projId)
    {
        // Build a filtering condition based on all the groupID associated to the current project in ProjValProjCategorySetup table
        while select GroupId from setupTbl
            group by GroupId
            where setupTbl.ProjId == projId
        {
            if (!groupStr)
            {
                groupStr = strFmt('(%1.%2 == "%3")',qbd.name(), fieldStr(ProjValProjCategorySetUp,GroupId), setupTbl.GroupId);
            }
            else
            {
                groupStr += strFmt(' || (%1.%2 == "%3")',qbd.name(), fieldStr(ProjValProjCategorySetUp,GroupId), setupTbl.GroupId);
            }
        }

        // Also filter by the ProjID and a blank ProjId to get only the categories for the groups associated with the current project
        list_ProjId.value(strFmt('(%1.%2 == "%3") || (%1.%2 == "")',qbd.name(), fieldStr(ProjValProjCategorySetUp,ProjId), projId));
    }

    if (groupStr)
    {
        list_GroupId.value(groupStr);
    }
    else
    {
        list_GroupId.value(SysQuery::valueEmptyString());
    }
}

void deleteCategoryFromProj()
{
    ProjCategory    locProjCategory;
    container       data;

    locProjCategory = projCategory_Empl_DS.getFirst(1);

    if (!locProjCategory)
    {
        data += projCategory_Empl.CategoryId;
    }
    else
    {
        while (locProjCategory)
        {
            data += locProjCategory.CategoryId;
            locProjCategory = projCategory_Empl_DS.getNext();
        }
    }
    ProjValElementServer::deleteCategoryFromProj(data, projId);
}

public void deleteGroupFromProj()
{
    ProjValProjCategoryTable    locProjValProjCategoryTable;
    container                   data;

    locProjValProjCategoryTable = projValProjCategoryTable_Group_DS.getFirst(1);

    if (!locProjValProjCategoryTable)
    {
        data += projValProjCategoryTable_Group.GroupId;
    }
    else
    {
        while (locProjValProjCategoryTable)
        {
            data += locProjValProjCategoryTable.GroupId;
            locProjValProjCategoryTable = projValProjCategoryTable_Group_DS.getNext();
        }
    }
    ProjValGroupServer::deleteProjCategoryGroupFromProj(data,projId);
}

