public void linkActive()
{
    ProjTable   projTable;
    ProjInvoiceTable tProjInvoiceTable;

    projId  = '';

    if (element.args().dataset() == tableNum(ProjTable))
    {
        projTable   = element.args().record();
        projId      = projTable.ProjId;
        element.buildQuery();
    }

    super();
}

