public void init()
{
    Query                q;
    QueryBuildDataSource qbr1, qbr2;

    q = new Query();

    super();

    qbr1 = q.addDataSource(tableNum(ProjValProjCategoryTable),'ProjValProjCategoryTable_group');
    qbr2 = qbr1.addDataSource(tableNum(ProjValProjCategorySetUp));

    qbr2.relations(true);

    qbr2.addSortField(fieldNum(ProjValProjCategorySetUp,GroupId));
    qbr2.addRange(fieldNum(ProjValProjCategorySetUp,GroupId)).value(SysQuery::valueNotEmptyString());

    group_ProjId    = qbr2.addRange(fieldNum(ProjValProjCategorySetUp, ProjId));
    this.query(q);
}

public void linkActive()
{
    ProjTable    projTable;
