        group_ProjId.value(projId);
    }
    super();
}

public void init()
{
    Query                q;
    QueryBuildDataSource qbr1, qbr2;

    q = new Query();

    super();

    qbr1 = q.addDataSource(tableNum(ProjValProjCategoryTable),'ProjValProjCategoryTable_NotGroup');
    qbr2 = qbr1.addDataSource(tableNum(ProjValProjCategorySetUp));

    qbr2.relations(true);
    qbr2.joinMode(JoinMode::NoExistsJoin);

    qbr2.addSortField(fieldNum(ProjValProjCategorySetUp,GroupId));
    notGroup_ProjId = qbr2.addRange(fieldNum(ProjValProjCategorySetUp, ProjId));
    this.query(q);
}

