public void init()
{
    Query                q;
    QueryBuildDataSource qbr1, qbr2;

    q = new Query();

    super();

    qbr1 = q.addDataSource(tableNum(ProjCategory));
    qbr2 = qbr1.addDataSource(tableNum(ProjValProjCategorySetUp));

    qbr2.relations(true);
    qbr2.joinMode(JoinMode::NoExistsJoin);
    qbr2.addSortField(fieldNum(ProjValProjCategorySetUp,GroupId));
    qbr2.addRange(fieldNum(ProjValProjCategorySetUp,GroupId)).value(SysQuery::valueEmptyString());

    notElement_ProjId    = qbr2.addRange(fieldNum(ProjValProjCategorySetUp, ProjId));
    if (isConfigurationkeyEnabled(configurationKeyNum(Project)))
    {
        qbr1.addRange(fieldNum(ProjCategory, psaCustPaymentRetention)).value(queryValue(NoYes::No));
    }

    this.query(q);
}

