void addGroupToCategory()
{
    ProjValEmplCategoryTable    locProjValEmplCategoryTable;
    container                   data;

    locProjValEmplCategoryTable = projValEmplCategoryTable_NotGroup_DS.getFirst(1);

    if (!locProjValEmplCategoryTable)
    {
        data += projValEmplCategoryTable_NotGroup.GroupId;
    }
    else
    {
        while (locProjValEmplCategoryTable)
        {
            data += locProjValEmplCategoryTable.GroupId;
            locProjValEmplCategoryTable = projValEmplCategoryTable_NotGroup_DS.getNext();
        }
    }

    ProjValGroupServer::addEmplCategoryGroupToCategory(data, categoryId);
}

void buildQuery()
{
    str     groupStr;
    ProjValEmplCategorySetUp    setupTbl;

    if (categoryId)
    {
        // Build a filtering condition based on all the groupID associated to the current category in ProjValEmplCategorySetup table
        while select GroupId from setupTbl
            group by GroupId
            where setupTbl.CategoryId == categoryId
        {
            if (!groupStr)
            {
                groupStr = strFmt('(%1.%2 == "%3")',qbd.name(), fieldStr(ProjValEmplCategorySetUp,GroupId), setupTbl.GroupId);
            }
            else
            {
                groupStr += strFmt(' || (%1.%2 == "%3")',qbd.name(), fieldStr(ProjValEmplCategorySetUp,GroupId), setupTbl.GroupId);
            }
        }

        // Also filter by the CategoryID and a blank categoryId to get all the resources for the groups associated with the current category
        list_CategoryId.value(strFmt('(%1.%2 == "%3") || (%1.%2 == "")',qbd.name(), fieldStr(ProjValEmplCategorySetUp,CategoryId), categoryId));
    }

    if (groupStr)
    {
        list_GroupId.value(groupStr);
    }
    else
    {
        list_GroupId.value(SysQuery::valueEmptyString());
    }
}

