void addResourceToCategoryIntercompany()
{
    ResourceView    locResource;
    Set             data = new Set(Types::Int64);
    Array           selectedResources;
    int             selectedResourceIndex;

    selectedResources = Resource_NotCategory_DS.recordsMarked();

    if (selectedResources.lastIndex())
    {
        for (selectedResourceIndex = 1; selectedResourceIndex <= selectedResources.lastIndex(); selectedResourceIndex++)
        {
            locResource = Resource_NotCategory_DS.cursor(selectedResources.value(selectedResourceIndex));               
            data.add(locResource.RecId);
        }
    }
    else
    {           
        data.add(resource_NotCategory.RecId);
    }

    ProjValElementServer::addResourceToEmplCategoryIntercompany(data,categoryId);
}

