    qbr1 = q.addDataSource(tableNum(ProjValEmplCategoryTable),'ProjValEmplCategoryTable_group');
    qbr2 = qbr1.addDataSource(tableNum(ProjValEmplCategorySetUp));

    qbr2.relations(true);

