public void executeQuery()
{
    QueryBuildRange         legalEntityRange;
    CompanyInfo             companyInfo;

    if (companyLookup.valueStr())
    {
        companyInfo = CompanyInfo::findDataArea(companyLookup.valueStr());
        legalEntityRange = SysQuery::findOrCreateRange(this.query().dataSourceTable(tableNum(ResourceLegalEntityView)), fieldNum(ResourceLegalEntityView, LegalEntity));
        legalEntityRange.value(queryValue(companyInfo.RecId));
    }

    super();
}

