public void init()
{
    Query                   q;
    QueryBuildDataSource    qbr1, qbr2;
    QueryBuildDataSource    qbdsResourceLegalEntityView;
    QueryBuildRange         legalEntityRange;

    q = new Query();

    super();

    qbr1 = q.addDataSource(tableNum(ResourceView));

    qbr1.addSortField(fieldNum(ResourceView, ResourceId));

    qbr2 = qbr1.addDataSource(tableNum(ProjValEmplCategorySetUp));
    qbr2.relations(false);
    qbr2.addLink(fieldNum(ResourceView, RecId), fieldNum(ProjValEmplCategorySetUp, Resource));
    qbr2.joinMode(JoinMode::NoExistsJoin);

    qbdsResourceLegalEntityView = qbr1.addDataSource(tableNum(ResourceLegalEntityView));
    qbdsResourceLegalEntityView.relations(false);
    qbdsResourceLegalEntityView.addLink(fieldNum(ResourceView, RecId), fieldNum(ResourceLegalEntityView, Resource));

    qbr2.addSortField(fieldNum(ProjValEmplCategorySetUp,GroupId));
    qbr2.addRange(fieldNum(ProjValEmplCategorySetUp,GroupId)).value(SysQuery::valueEmptyString());

    notElement_CategoryId    = qbr2.addRange(fieldNum(ProjValEmplCategorySetUp, CategoryId));

    // Limit to the current company
    legalEntityRange = qbdsResourceLegalEntityView.addRange(fieldNum(ResourceLegalEntityView, LegalEntity));
    legalEntityRange.value(queryValue(CompanyInfo::current()));

    this.query(q);
}

