void buildQuery()
{
    str groupStr;
    ProjValEmplProjSetup setupTbl;

    if (projId)
    {
        // Build a filtering condition based on all the groupID associated  the current Project in ProjValEmplProjSetup table
        while select GroupId from setupTbl
            group by GroupId
            where setupTbl.Projid == projId
        {
            if (!groupStr)
            {
                groupStr = strFmt('(%1.%2 == "%3")', formDataSourceStr(ProjValSetupEmplProj, OverviewTab_ProjValEmplProjSetup), fieldStr(ProjValEmplProjSetup,GroupId), setupTbl.GroupId);
            }
            else
            {
                groupStr += strFmt(' || (%1.%2 == "%3")', formDataSourceStr(ProjValSetupEmplProj, OverviewTab_ProjValEmplProjSetup), fieldStr(ProjValEmplCategorySetUp,GroupId), setupTbl.GroupId);
            }
        }

        // Also filter by the ProjID or a blank ProjId to get only the resources for the groups associated with the current project
        OverviewTabProjectRange.value(strFmt('(%1.%2 == "%3") || (%1.%2 == "")', formDataSourceStr(ProjValSetupEmplProj, OverviewTab_ProjValEmplProjSetup), fieldStr(ProjValEmplProjSetup,Projid), projId));
    }

    if (groupStr)
    {
        OverviewTabGroupRange.value(groupStr);
    }
    else
    {
        OverviewTabGroupRange.value(SysQuery::valueEmptyString());
    }
}

