            dataArea = dataArea == '' ? curExt() : dataArea;

            SysQuery::findOrCreateRange(this.queryBuildDataSource(), fieldNum(ResourceView, ResourceCompanyId)).value(dataArea);
            SysQuery::findOrCreateRange(qbdsHcmEmployment, fieldNum(HcmEmployment, LegalEntity)).value(queryValue(CompanyInfo::findDataArea(dataArea).RecId));
        }

