/// <summary>
/// Set the resource status range based on the <c>HcmWorkerStatusRange</c> enumerator value.
/// </summary>
/// <param name="_statusRange">
/// <c>HcmWorkerStatusRange</c> enumerator value.
/// </param>
public void setResourceStatusRange(HcmWorkerStatusRange _statusRange)
{
    switch (_statusRange)
    {
        case HcmWorkerStatusRange::Employed:
            // Include only active resources
            includeActive = true;
            includePending = false;
            includeTerminated = false;
            break;

        case HcmWorkerStatusRange::Pending:
            // Include only pending resources
            includeActive = false;
            includePending = true;
            includeTerminated = false;
            break;

        case HcmWorkerStatusRange::Terminated:
            // Include only terminated resources
            includeActive = false;
            includePending = false;
            includeTerminated = true;
            break;

        case HcmWorkerStatusRange::EmployedAndPending:
            // Include both employed and pending resources
            includeActive = true;
            includePending = true;
            includeTerminated = false;
            break;

        case HcmWorkerStatusRange::EmployedAndTerminated:
            // Include both employed and terminated resources
            includeActive = true;
            includePending = false;
            includeTerminated = true;
            break;

        case HcmWorkerStatusRange::All:
            // Include all resources
            includeActive = true;
            includePending = true;
            includeTerminated = true;
            break;
    }
}

public void setEmploymentStatusFilters()
{
    utcdatetime now = DateTimeUtil::utcNow();
    this.setResourceStatusRange(EmploymentStatus.selection());

    QueryBuildRange validFromRange   = SysQuery::findOrCreateRange(qbdsHcmEmployment, fieldNum(HcmEmployment, ValidFrom));
    QueryBuildRange validToRange     = SysQuery::findOrCreateRange(qbdsHcmEmployment, fieldNum(HcmEmployment, ValidTo));

    if (includeActive && includePending && includeTerminated)
    {
        this.query().validTimeStateDateTimeRange(DateTimeUtil::minValue(), DateTimeUtil::maxValue());
        validFromRange.value(SysQuery::valueUnlimited());
        validToRange.value(SysQuery::valueUnlimited());
    }
    else if (includeActive && includePending)
    {
        this.query().validTimeStateDateTimeRange(now, DateTimeUtil::maxValue());
        validFromRange.value(SysQuery::valueUnlimited());
        validToRange.value(SysQuery::valueUnlimited());
    }
    else if (includeTerminated && includeActive)
    {
        this.query().validTimeStateDateTimeRange(DateTimeUtil::minValue(), now);
        validFromRange.value(SysQuery::valueUnlimited());
        validToRange.value(SysQuery::valueUnlimited());
    }
    else if (includeActive)
    {
        this.query().validTimeStateDateTimeRange(now, now);
        validFromRange.value(SysQuery::valueUnlimited());
        validToRange.value(SysQuery::valueUnlimited());
    }
    else if (includeTerminated)
    {
        this.query().validTimeStateDateTimeRange(DateTimeUtil::minValue(), DateTimeUtil::utcNow());
        validFromRange.value(SysQuery::valueUnlimited());
        validToRange.value(SysQueryRangeUtil::lessThanUtcNow());
    }
    else if (includePending)
    {
        this.query().validTimeStateDateTimeRange(HcmDateTimeUtil::startOfCurrentDay(), DateTimeUtil::maxValue());
        validFromRange.value(SysQueryRangeUtil::greaterThanUtcNow());
        validToRange.value(SysQuery::valueUnlimited());
    }
    else
    {
        this.query().validTimeStateDateTimeRange(HcmDateTimeUtil::startOfCurrentDay(), DateTimeUtil::maxValue());
        validFromRange.value(SysQuery::valueUnlimited());
        validToRange.value(SysQuery::valueUnlimited());
    }
}

