void clicked()
{
    void addEmplProjGroupToProj(ProjValEmplProjGroupId _groupId, ProjId _projId)
    {
        ProjValEmplProjSetup projValEmplProjSetup;

        projValEmplProjSetup.initValue();
        projValEmplProjSetup.GroupId = _groupId;
        projValEmplProjSetup.Projid = projId;

        if (projValEmplProjSetup.validateWrite())
        {
            ttsbegin;
            projValEmplProjSetup.insert();
            ttscommit;
        }
    }

    super();

    ProjValEmplProjTable projValEmplProjTable = ValidationTab_Available_DS.getFirst(1);

    if (!projValEmplProjTable)
    {
        addEmplProjGroupToProj(ValidationTab_Available.GroupId, projId);
    }
    else
    {
        while (projValEmplProjTable)
        {
            addEmplProjGroupToProj(projValEmplProjTable.GroupId, projId);
            projValEmplProjTable = ValidationTab_Available_DS.getNext();
        }
    }

    ValidationTab_Selected_ds.research();
    ValidationTab_Available_ds.research();
}

void clicked()
{
    super();

    ProjValEmplProjSetup    projValEmplProjTable = ValidationTab_Selected_DS.getFirst(1);

    if (!ProjValEmplProjTable)
    {
        delete_from projValEmplProjTable where projValEmplProjTable.RecId == ValidationTab_Selected.RecId;
    }
    else
    {
        while (projValEmplProjTable)
        {
            projValEmplProjTable.delete();
            projValEmplProjTable = ValidationTab_Selected_DS.getNext();
        }
    }

    ValidationTab_Selected_ds.executeQuery();
    ValidationTab_Available_ds.executeQuery();
}

