void clicked()
{
    super();

    DataAreaId  companyLookupText = companyLookup.text();
    Array       selectedResources = ResourceTab_Available_DS.recordsMarked();

    if (selectedResources.lastIndex())
    {
        Common record;

        for (int selectedResourceIndex = 1; selectedResourceIndex <= selectedResources.lastIndex(); selectedResourceIndex++)
        {
            record = ResourceTab_Available_DS.cursor(selectedResources.value(selectedResourceIndex));
            element.addResourceToProject(companyLookupText, record.RecId, projId);
        }
    }
    else
    {
        element.addResourceToProject(companyLookupText, ResourceTab_Available.RecId, projId);
    }
    ResourceTab_Selected_ds.executeQuery();
    ResourceTab_Available_ds.executeQuery();
}

void clicked()
{
    super();

    ProjValEmplProjSetup projValEmplProjSetup;
    Array   selectedResources = ResourceTab_Selected_DS.recordsMarked();

    if (selectedResources.lastIndex())
    {
        for (int selectedResourceIndex = 1; selectedResourceIndex <= selectedResources.lastIndex(); selectedResourceIndex++)
        {
            projValEmplProjSetup = ResourceTab_Selected_DS.cursor(selectedResources.value(selectedResourceIndex));
            projValEmplProjSetup.delete();
        }
    }
    else
    {
        // using delete_from to avoid confirmation prompt
        element.deleteProjValEmplProjSetup(ResourceTab_Selected.RecId);
    }

    ResourceTab_Selected_ds.executeQuery();
    ResourceTab_Available_ds.executeQuery();
}
