void addCategoryToGroup()
{
    ProjCategory   projCategory;
    container   data;

    if (projValEmplCategoryTable.GroupId)
    {
        projCategory = projCategory_NotGroup_DS.getFirst(1);

        if (!projCategory)
        {
            data    += projCategory_NotGroup.CategoryId;
        }
        else
        {
            while (projCategory)
            {
                data += projCategory.CategoryId;
                projCategory = projCategory_NotGroup_DS.getNext();
            }
        }

        ProjValTableServer::addCategoryToEmplCategory(data,projValEmplCategoryTable.GroupId);
    }
}

