public void init()
{
    #resAppl

    resPerfEnhancementFeatureEnable = ResUtil::isPerformanceEnhancementFeatureEnabled(true);

    // Setup ProjValEmplCategorySetup_Group datasource depending on whether the resource scheduling performance enhancement feature is enabled.
    // It needs to be configured before super(). FormBuildDataSource objects are look up by the datasource name.
    FormBuildDataSource fbds_ProjValEmplCategorySetup_Group = element.getFormBuildDataSource('ProjValEmplCategorySetup_Group');
    if (resPerfEnhancementFeatureEnable)
    {
        fbds_ProjValEmplCategorySetup_Group.joinSource(element.getFormBuildDataSource('EnhancedEffectiveDate_Group').id());
    }
    else
    {
        fbds_ProjValEmplCategorySetup_Group.joinSource(element.getFormBuildDataSource('ResourceLegalEntity_Group').id());
    }

    super();

    companyLookup.text(curext());
}

private FormBuildDataSource getFormBuildDataSource(str _fromBuildDataSourceName)
{
    FormBuildDataSource fbds;
    int dataSourceCounter;

    while (dataSourceCounter < element.form().dataSourceCount())
    {
        dataSourceCounter++;
        if (element.form().dataSource(dataSourceCounter).name() == _fromBuildDataSourceName)
        {
            fbds = element.form().dataSource(dataSourceCounter);
            break;
        }
    }

    return fbds;
}

