public int active()
{
    int ret;

    ret = super();

    if (projValEmplCategoryTable.GroupId)
    {
        emplGroup_GroupId.value(projValEmplCategoryTable.GroupId);
        emplGroup_CategoryId.value(SysQuery::valueEmptyString());
        emplGroup_Resource.value(SysQuery::valueNotEmptyString());

        emplNotGroup_GroupId.value(projValEmplCategoryTable.GroupId);
        emplNotGroup_CategoryId.value(SysQuery::valueEmptyString());
        emplNotGroup_Resource.value(SysQuery::valueNotEmptyString());

        categoryGroup_GroupId.value(projValEmplCategoryTable.GroupId);
        categoryGroup_EmplId.value(SysQuery::valueEmptyString());
        categoryGroup_CategoryId.value(SysQuery::valueNotEmptyString());

        categoryNotGroup_GroupId.value(projValEmplCategoryTable.GroupId);
        categoryNotGroup_EmplId.value(SysQuery::valueEmptyString());
        categoryNotGroup_CategoryId.value(SysQuery::valueNotEmptyString());
    }
    else
    {
        emplGroup_GroupId.value(SysQuery::valueNotEmptyString());
        emplGroup_CategoryId.value(SysQuery::valueEmptyString());
        emplGroup_Resource.value(SysQuery::valueEmptyString());

        emplNotGroup_GroupId.value(SysQuery::valueEmptyString());
        emplNotGroup_CategoryId.value(SysQuery::valueEmptyString());
        emplNotGroup_Resource.value(SysQuery::valueEmptyString());

        categoryGroup_GroupId.value(SysQuery::valueNotEmptyString());
        categoryGroup_EmplId.value(SysQuery::valueEmptyString());
        categoryGroup_CategoryId.value(SysQuery::valueEmptyString());

        categoryNotGroup_GroupId.value(SysQuery::valueEmptyString());
        categoryNotGroup_EmplId.value(SysQuery::valueEmptyString());
        categoryNotGroup_CategoryId.value(SysQuery::valueEmptyString());
    }

    return ret;
}

