public void executeQuery()
{
    switch (setupCatSelectedActiveAll.selection())
    {
        case ProjActiveAll::All :
            categoryGroup_ActiveAll.value('');
            break;

        case ProjActiveAll::Active:
            categoryGroup_ActiveAll.value(Global::queryValue(NoYes::Yes));
            break;
    }

    if (ProjValEmplCategoryTable.GroupId == '')
    {
        categoryGroup_GroupId.value(SysQuery::valueNotEmptyString());
    }

    super();
}

public void init()
{
    Query                q;
    QueryBuildDataSource qbr1, qbr2;

    q = new Query();

    super();

    qbr1 = q.addDataSource(tableNum(ProjCategory),'ProjCategory_Group');
    qbr2 = qbr1.addDataSource(tableNum(ProjValEmplCategorySetUp));

    qbr2.relations(true);

    categoryGroup_EmplId = qbr2.addRange(fieldNum(ProjValEmplCategorySetUp, Resource));
    categoryGroup_CategoryId = qbr2.addRange(fieldNum(ProjValEmplCategorySetUp, CategoryId));
    categoryGroup_GroupId = qbr2.addRange(fieldNum(ProjValEmplCategorySetUp, GroupId));

    this.query(q);

    categoryGroup_ActiveAll = this.query().dataSourceTable(tableNum(ProjCategory)).addRange(fieldNum(ProjCategory, Active));
}

