public void init()
{
    Query                q;
    QueryBuildDataSource qbr1, qbr2;
    QueryBuildRange      qbr;

    q = new Query();

    super();

    qbr1 = q.addDataSource(tableNum(ProjCategory),'ProjCategory_Group');

    qbr  = qbr1.addRange(fieldNum(ProjCategory,CategoryType));
    qbr.value((queryValue(ProjCategoryType::Revenue)) + ',' + (queryValue(ProjCategoryType::Hour)) + ',' + (queryValue(ProjCategoryType::Cost)));

    qbr2 = qbr1.addDataSource(tableNum(ProjValEmplCategorySetUp));

    qbr2.relations(true);

    qbr2.joinMode(JoinMode::NoExistsJoin);

    categoryNotGroup_EmplId     = qbr2.addRange(fieldNum(ProjValEmplCategorySetUp, Resource));
    categoryNotGroup_CategoryId = qbr2.addRange(fieldNum(ProjValEmplCategorySetUp,CategoryId));
    categoryNotGroup_GroupId   = qbr2.addRange(fieldNum(ProjValEmplCategorySetUp, GroupId));

    this.query(q);

    categoryNotGroup_ActiveAll = this.query().dataSourceTable(tableNum(ProjCategory)).addRange(fieldNum(ProjCategory, Active));
}

public void init()
{

    super();

    QueryBuildDataSource queryBuildDataSource = projValEmplCategorySetUp_Group_ds.query().dataSourceName(identifierStr(ProjValEmplCategorySetUp_Group));

    if (resPerfEnhancementFeatureEnable)
    {
        queryBuildDataSource.addLink(fieldNum(ResEffectiveDateTimeRange, Resource), fieldNum(ProjValEmplCategorySetUp, Resource));
    }
    else
    {
        queryBuildDataSource.addLink(fieldNum(ResourceLegalEntityView, Resource), fieldNum(ProjValEmplCategorySetUp, Resource));
    }

    emplGroup_CategoryId = queryBuildDataSource.addRange(fieldNum(ProjValEmplCategorySetUp, CategoryId));
    emplGroup_Resource = queryBuildDataSource.addRange(fieldNum(ProjValEmplCategorySetUp, Resource));
    emplGroup_GroupId = queryBuildDataSource.addRange(fieldNum(ProjValEmplCategorySetUp, GroupId));

    emplGroup_GroupId.value(SysQuery::valueNotEmptyString());
    emplGroup_CategoryId.value(SysQuery::valueEmptyString());
    emplGroup_Resource.value(SysQuery::valueEmptyString());
}

