public void executeQuery()
{
    utcdatetime now = DateTimeUtil::utcNow();
    switch (setupEmplAvailableActiveAll.selection())
    {
        case ProjActiveAll::All:
            this.query().validTimeStateDateTimeRange(DateTimeUtil::minValue(), DateTimeUtil::maxValue());
            break;

        case ProjActiveAll::Active:
            this.query().validTimeStateDateTimeRange(now, now);
            break;
    }

    date today = DateTimeUtil::getSystemDate(DateTimeUtil::getCompanyTimeZone());
    notGroupResourceLEValidFromRange.value(queryRange(dateNull(), today));

    // To be certain that this will be larger than the system max date for range comparsion.
    date dateMaxPlusOne = dateMax() + 1;
    notGroupResourceLEValidToRange.value(queryRange(today, dateMaxPlusOne));

    QueryBuildRange resourceDataAreaIdRange = SysQuery::findOrCreateRange(this.query().dataSourceTable(tableNum(ResourceView)), fieldNum(ResourceView, ResourceCompanyId));

    if (companyLookup.text())
    {
        CompanyInfo companyInfo = CompanyInfo::findDataArea(companyLookup.text());
        resourceDataAreaIdRange.value(queryValue(companyInfo.DataArea));
    }
    else
    {
        resourceDataAreaIdRange.value(queryValue(curExt()));
    }

    super();
}

