[Form]
public class ProjWBSActivityDetails extends FormRun
{
    ProjTable           projTable;
    ProjPlanVersion     projPlanVersionRecord;
    SmmActivityNumber   activityNumber;
    boolean             isTemplate;
    boolean             isParent;
    boolean             isHiearchyLinkedToQuotation;
    [FormObservableAttribute]
    Hours               activityRemainingHours;
    [FormObservableAttribute]
    Hours               activityAssignedHours;
    [FormObservableAttribute]
    str                 staffingStatus;
    [FormObservableAttribute]
    NonNegativeTotal    summarySalesPrice;
    [FormObservableAttribute]
    NonNegativeTotal    summaryCostPrice;
    [FormObservable]
    private ProjWBSType typeOfWBS;

    SalesTableForm_ProjectSalesItem salesTableForm;
    InventDimCtrl_Frm_Mov           inventDimFormSetup;

}

public void close()
{
    if (!isTemplate && !isHiearchyLinkedToQuotation && !isParent && salesTableForm)
    {
        salesTableForm.createInterCompanySalesOrderForItemRequirement();
    }

    FormRun callerForm = element.args().caller();

    super();

    if (callerForm)
    {
        // Project
        if (strCmp(callerForm.name(), formStr(ProjWorkBreakdownStructureV2)) == 0)
        {
            if (formHasMethod(callerForm, identifierStr(updateDataSourcesKeepCursor)))
            {
                callerForm.updateDataSourcesKeepCursor();
            }
        }
    }
}

