    PSAActivityEstimates_DS.refresh();
}

/// <summary>
/// Assigning the record context and control settings during initialization.
/// </summary>
public void init()
{
    super();

    if (element.args().dataset() == tableNum(ProjPlanVersion))
    {
        projPlanVersionRecord = element.args().record();
        typeOfWBS = ProjPlanVersionDetail::findByHierarchyId(projPlanVersionRecord.HierarchyId).getWBSType();

        this.design().caption(strFmt("@SYS4008775", projPlanVersionRecord.TaskName));

        ProjPlanVersion_DS.query()
            .dataSourceTable(tableNum(ProjPlanVersion))
                .addRange(fieldNum(ProjPlanVersion, RecId))
                    .value(SysQuery::value(projPlanVersionRecord.RecId));

        // Activity number is available only for the published tasks
        activityNumber = ProjPlanVersion::getActivityNumber(projPlanVersionRecord.HierarchyId, projPlanVersionRecord.HierarchyTaskId);
        element.getActivityRelatedRecords(activityNumber);

        boolean editResourceCategory = 
            !(ActivityFacade::existsAnyAssignmentForActivity(PSAActivityEstimates::findActivityRecId(projPlanVersionRecord.HierarchyId, projPlanVersionRecord.HierarchyTaskId))
                || ProjPlanVersionAssignment::hasReservationByHierarchyAndTaskId(projPlanVersionRecord.HierarchyId, projPlanVersionRecord.HierarchyTaskId));

        PSAActivityEstimates_ds.object(fieldnum(PSAActivityEstimates, ResourceCategory)).allowEdit(editResourceCategory);

        element.hideTabsAndFieldsForParentTasks();

        if (!isParent)
        {
            element.showHideTabsBasedOnTheTaskStatus();
        }

        element.calcPriceSummaries();
        element.updateDesign(InventDimFormDesignUpdate::Init);
    }
}

private void getActivityRelatedRecords(smmActivityNumber _activityNumber)
{
    if (_activityNumber)
    {
        ProjPlanVersion_ActivityNumber.text(_activityNumber);
        ProjPlanVersion_ActivityNumber.visible(true);

        ResCommitType commitType = ProjPlanVersionDetail::isQuotationHierarchy(projPlanVersionRecord.HierarchyId) ? ResCommitType::SoftBook : ResCommitType::HardBook;

        ActivityRecId activityRecId = smmActivities::find(_activityNumber).RecId;
        staffingStatus = enum2str(projPlanVersionRecord.TaskSchedulingStatus);
        activityAssignedHours = ActivityFacade::getAssignedCapacityInHours(activityRecId, commitType, false);
        activityRemainingHours = projPlanVersionRecord.TaskEffort - activityAssignedHours;
    }
    else
    {
        staffingStatus = enum2str(PSAScheduled::NotScheduled);
        activityAssignedHours = 0;
        activityRemainingHours = projPlanVersionRecord.TaskEffort;
    }
}

private void hideTabsAndFieldsForParentTasks()
{
    ProjPlanVersion projPlanVersionParent;

    select count(recid) from projPlanVersionParent
            where projPlanVersionParent.ParentHierarchyTaskId == projPlanVersionRecord.HierarchyTaskId
            && projPlanVersionParent.HierarchyId == projPlanVersionRecord.HierarchyId;

    if ((projPlanVersionParent) && (projPlanVersionParent.RecId > 0))
    {
        ProjPlanVersion_DS.allowEdit(false);
        ProjPlanVersion_TaskPriority.visible(false);
        ProjPlanVersion_ActivityTaskTimeType.visible(false);
        TaskCategory_IsCategoryMandatory.visible(false);
        TaskSchedulingGroup.visible(false);
        ResourceScheduling.visible(false);
        PriceEstimatesSummary.visible(false);
        TaskCategory.visible(false);
        PSAActivityEstimates_DS.allowEdit(false);
        DetailsTab.visible(false);
        element.design().dialogSize(0); //Small
        isParent = true;
    }
}

    private void showHideTabsBasedOnTheTaskStatus()
    {
        // The HierarchyId of ProjPlanVersion and HierarchyTreeTable are not same.
        // The Hierarchy Table contains the initial version of the HierarchyId
        HierarchyIdBase hierarchyId = HierarchyTreeTable::findRecId(ProjPlanVersion::getRootTask(projPlanVersionRecord.HierarchyID).HierarchyTreeTableRefRecId).HierarchyId;

        // Do not show item requirements for the template task.
        if (Hierarchy::find(hierarchyId).IsTemplate)
        {
            isTemplate = true;
            ItemRequirements.visible(false);
        }
        else
        {
            Common linkedTable = HierarchyLinkTable_Extension::findLinkedTableByHierarchyId(hierarchyId);

			if (linkedTable is SalesQuotationTable)
            {
                SalesQuotationTable salesQuotationTable = linkedTable as SalesQuotationTable;

				isHiearchyLinkedToQuotation = true;
				if (salesQuotationTable.ProjIdRef)
                {
					projTable = ProjTable::find(salesQuotationTable.ProjIdRef);
                }

				ItemRequirements.visible(false);

                if (!salesQuotationTable.isWBSEditable())
                {
                    ProjPlanVersion_ds.allowEdit(false);
                    element.disableEstimatesDataSource();
                    element.disableSalesLineDataSource();
                }

                return;
            }
			else
            {
                isHiearchyLinkedToQuotation = false;
                projTable = linkedTable as ProjTable;
            }

            ProjPlanVersionDetail   projPlanVersionDetail;

            select firstonly VersionType, VersionId from projPlanVersionDetail
            where projPlanVersionDetail.HierarchyId == projPlanVersionRecord.HierarchyId;

            /* If the WBS task status is
                a. Draft and Version > 1, allow user to enter estimated costs and revenue + item requirements
                b. Draft and Version = 1, allow user to enter estimated costs and revenue only
                c. Other than Draft and version > 1, allow user to enter only item requirements.
            */

            if (projPlanVersionDetail.VersionType == ProjPlanVersionType::Draft && projPlanVersionDetail.VersionId > 1)
            {
                element.initializeItemRequirements();
            }
            else if (projPlanVersionDetail.VersionType == ProjPlanVersionType::Draft && projPlanVersionDetail.VersionId == 1)
            {
                element.disableSalesLineDataSource();
                element.applyRanges();
            }
            else
            {
                ProjPlanVersion_DS.allowEdit(false);
                element.disableEstimatesDataSource();
                element.disableSalesLineDataSource();
                element.applyRanges();
            }

            if (element.args().parm() == classStr(ProjWbsTrackingServerActions))
            {
                DetailsTab.visible(false);
                element.design().dialogSize(1); // Medium
                element.disableEstimatesDataSource();
                element.disableSalesLineDataSource();
            }
        }
    }

private void disableEstimatesDataSource()
{
    PSAActivityEstimates_DS.allowCreate(false);
    PSAActivityEstimates_DS.allowDelete(false);
    PSAActivityEstimates_DS.allowEdit(false);
}

private void disableSalesLineDataSource()
{
    SalesLine_DS.allowCreate(false);
    SalesLine_DS.allowDelete(false);
    SalesLine_DS.allowEdit(false);
}

private void initializeItemRequirements()
{
    salesTableForm = SalesTableForm::construct(SalesTableFormId::ProjectSalesItem, projTable);
    element.applyRanges();
}

private void applyRanges()
{
    QueryBuildDataSource salesLineDataSource = SalesLine_DS.query().dataSourceTable(TableNum(SalesLine));

    salesLineDataSource.clearDynalinks();
    salesLineDataSource.addRange(fieldNum(SalesLine, SalesType)).value(queryValue(SalesType::ItemReq));
    salesLineDataSource.addRange(fieldNum(SalesLine, ProjId)).value(queryValue(projTable.ProjId));
    salesLineDataSource.addRange(fieldNum(SalesLine, ActivityNumber)).value(queryValue(activityNumber));
}

protected void setControlEnabledState(QuotationProjTransType _projTransType)
{
    ProductDimension.enabled(false);

    switch (_projTransType)
    {
        case QuotationProjTransType::Item:
            PSAActivityEstimates_DS.object(fieldNum(PSAActivityEstimates, ItemId)).allowEdit(true);
            PSAActivityEstimates_DS.object(fieldNum(PSAActivityEstimates, SalesCategory)).allowEdit(true);
            PSAActivityEstimates_DS.object(fieldNum(PSAActivityEstimates, ResourceCategory)).allowEdit(true);
            PSAActivityEstimates_DS.object(fieldNum(PSAActivityEstimates, Quantity)).allowEdit(true);
            PSAActivityEstimates_DS.object(fieldNum(PSAActivityEstimates, UnitCostPrice)).allowEdit(true);

            if (PSAActivityEstimates.ItemId != '')
            {
                PSAActivityEstimates_DS.object(fieldNum(PSAActivityEstimates, SalesCategory)).allowEdit(false);
                ProductDimension.enabled(true);
            }
            else if (PSAActivityEstimates.SalesCategory != 0)
            {
                PSAActivityEstimates_DS.object(fieldNum(PSAActivityEstimates, ItemId)).allowEdit(false);
            }
            break;

        case QuotationProjTransType::Hour:
            PSAActivityEstimates_DS.object(fieldNum(PSAActivityEstimates, ItemId)).allowEdit(false);
            PSAActivityEstimates_DS.object(fieldNum(PSAActivityEstimates, SalesCategory)).allowEdit(false);
            PSAActivityEstimates_DS.object(fieldNum(PSAActivityEstimates, ResourceCategory)).allowEdit(true);
            PSAActivityEstimates_DS.object(fieldNum(PSAActivityEstimates, Quantity)).allowEdit(true);
            PSAActivityEstimates_DS.object(fieldNum(PSAActivityEstimates, UnitCostPrice)).allowEdit(true);
            break;

        case QuotationProjTransType::Expense:
            PSAActivityEstimates_DS.object(fieldNum(PSAActivityEstimates, ItemId)).allowEdit(false);
            PSAActivityEstimates_DS.object(fieldNum(PSAActivityEstimates, SalesCategory)).allowEdit(false);
            PSAActivityEstimates_DS.object(fieldNum(PSAActivityEstimates, ResourceCategory)).allowEdit(false);
            PSAActivityEstimates_DS.object(fieldNum(PSAActivityEstimates, Quantity)).allowEdit(true);
            PSAActivityEstimates_DS.object(fieldNum(PSAActivityEstimates, UnitCostPrice)).allowEdit(true);
            break;

        case QuotationProjTransType::Fee:
            PSAActivityEstimates_DS.object(fieldNum(PSAActivityEstimates, ItemId)).allowEdit(false);
            PSAActivityEstimates_DS.object(fieldNum(PSAActivityEstimates, SalesCategory)).allowEdit(false);
            PSAActivityEstimates_DS.object(fieldNum(PSAActivityEstimates, ResourceCategory)).allowEdit(true);
            PSAActivityEstimates_DS.object(fieldNum(PSAActivityEstimates, Quantity)).allowEdit(false);
            PSAActivityEstimates_DS.object(fieldNum(PSAActivityEstimates, UnitCostPrice)).allowEdit(false);
            break;
    }
}

