private void calcPriceSummaries()
{
    PSAActivityEstimates localPSAActivityEstimates;

    select sum(TotalSalesPrice), sum(TotalCostPrice) from localPSAActivityEstimates
        where localPSAActivityEstimates.ProjPlanHierarchyId == projPlanVersionRecord.HierarchyId
        && localPSAActivityEstimates.ProjPlanHierarchyTaskId == projPlanVersionRecord.HierarchyTaskId;

    summarySalesPrice = localPSAActivityEstimates.TotalSalesPrice;
    summaryCostPrice = localPSAActivityEstimates.TotalCostPrice;
}

/// <summary>
/// This method will be called by the InventDimParmFixed form.
/// </summary>
public Object inventDimSetupObject()
{
    return inventDimFormSetup;
}

private void updateDesign(InventDimFormDesignUpdate mode)
{
    InventMovement movement = InventMovement::construct(salesLine);

    switch (mode)
    {
        case InventDimFormDesignUpdate::Init        :
            if (!inventDimFormSetup)
            {
                inventDimFormSetup  = InventDimCtrl_Frm_Mov::newFromFormMovement(element, movement);
            }

        case InventDimFormDesignUpdate::Active      :
            inventDimFormSetup.formActiveSetup(movement.inventDimGroupSetup(),movement);
            inventDimFormSetup.formSetControls(true);
            break;

        case InventDimFormDesignUpdate::FieldChange :
            inventDimFormSetup.formActiveSetup(movement.inventDimGroupSetup(),movement);
            inventDimFormSetup.formSetControls(false);
            break;

        default :
            throw error(strFmt("@SYS54195",funcName()));
    }
}

private smmActivities getAssociatedsmmActivitiesBuffer()
{
    smmActivities smmActivities;
    HierarchyTreeTable hierarchyTreeTable;

    select forupdate smmActivities
        exists join hierarchyTreeTable
            where hierarchyTreeTable.RecId == ProjPlanVersion.HierarchyTreeTableRefRecId
                && hierarchyTreeTable.RefRecId == smmActivities.RecId;

    return smmActivities;
}

