public boolean modified()
{
    boolean ret;

    ret = super();

    if (ret && typeOfWBS == ProjWBSType::ProjectQuotation)
    {
        smmActivities smmActivities = element.getAssociatedsmmActivitiesBuffer();
        if (smmActivities)
        {
            ttsbegin;
            smmActivities.UserMemo = ProjPlanVersion.UserMemo;
            smmActivities.update();
            ttscommit;
        }
    }

    return ret;
}

