public class ProjYearEndProcess extends FormRun
{
    ProjYearEndProcess  projYearEndProcess;
    boolean             canExecuteQuery;
    Query               yearEndProcessQuery;
}

public void callBatchDialog()
{
    MenuFunction menuFunction;
    Args         args = new Args(this);

    args.caller(element);

    menuFunction = new MenuFunction(menuitemActionStr(BatchDialog), MenuItemType::Action);

    menuFunction.run(args);
}

public void callYearEndProcess()
{
    boolean                     existUncommitted;
    FiscalCalendarYearRecId     fiscalYearRecId;
    List                        projBudgetLineSelected = new List(Types::Record);
    FiscalCalendarRecId         fiscalCalendarRecId = Ledger::fiscalCalendar(CompanyInfo::current());

    boolean chkRequiredFields = fromForecastModelId.validate();

    boolean isAnyRecordSelected()
    {
        if (!projTable_ds.anyMarked())
        {
            return checkFailed("@SYS328439");
        }
        return true;
    }

    if (!toForecastModelId.text())
    {
        chkRequiredFields = toForecastModelId.validate();
    }

    this.setValues();
    if (projYearEndProcess.validate() && chkRequiredFields && isAnyRecordSelected())
    {
        if (projTable_ds.anyMarked() && (Box::yesNo("@SYS329462", DialogButton::No) == DialogButton::Yes))
        {
            fiscalYearRecId     = FiscalCalendarYear::findByCalendarYearName(fiscalCalendarRecId, proj_FiscalCalendarYear.text()).RecId;

            projTable_ds.first();
            do
            {
                if (projTable_ds.mark())
                {
                    projBudgetLineSelected.addEnd(projBudgetLine);

                    if (!existUncommitted)
                    {
                        existUncommitted = ProjYearEndProcess::existUncommittedRevisions(projBudgetLine.RecId, fiscalYearRecId);
                    }
                }
            }
            while (projTable_ds.next());

            if (!existUncommitted || (existUncommitted && Box::yesNo("@SYS327591", DialogButton::Yes) == DialogButton::Yes))
            {
                projYearEndProcess.parmSelectedProjBudgetLine(projBudgetLineSelected);
                this.closeOk();
            }
        }
    }
}

public void close()
{
    this.setValues();
    super();
}

private void getValues()
{
    FiscalCalendarRecId fiscalCalendarRecId = Ledger::fiscalCalendar(CompanyInfo::current());
    date                nextYearDate;

    if (xSysLastValue::existLast(projYearEndProcess))
    {
        xSysLastValue::getLast(projYearEndProcess);
        chkBoxCarryForward.value(projYearEndProcess.parmCarryForward());
        chkBoxGLbudget.value(projYearEndProcess.parmAdjustGL());
        projectBudgetYear.text(projYearEndProcess.parmProjFiscalYearName());
        fiscalCalendar.text(projYearEndProcess.parmFiscalCalendarID());
        proj_FiscalCalendarYear.text(projYearEndProcess.parmFiscalYearName());
        proj_FiscalCalendarPeriod.text(projYearEndProcess.parmFiscalCalendarPeriod());
        fromForecastModelId.text(projYearEndProcess.parmFromForecastModel());
        toForecastModelId.text(projYearEndProcess.parmToForecastModel());
        tansferSalesCurrencyChkBx.value(projYearEndProcess.parmSalesCurrency());
        showZeroRemainingChkBx.value(projYearEndProcess.parmShowZeroRemaining());
        includeProfitAndLossChkBx.value(projYearEndProcess.parmIncludeProfitAndloss());
        includeWIPChkBx.value(projYearEndProcess.parmIncludeWIP());
        includePayrollAllocationChkBx.value(projYearEndProcess.parmIncludePayrollAllocation());
    }

    if (!fiscalCalendar.text())
    {
        fiscalCalendar.text(FiscalCalendar::find(fiscalCalendarRecId).CalendarId);
    }
    if (!proj_FiscalCalendarYear.text())
    {
        nextYearDate = DateTimeUtil::date(DateTimeUtil::addYears(DateTimeUtil::utcNow(),1));
        proj_FiscalCalendarYear.text(FiscalCalendarYear::findYearByCalendarDate(fiscalCalendarRecId, nextYearDate).yearName());
        proj_FiscalCalendarPeriod.text(LedgerFiscalCalendar::findFirstOpenPeriodInYear(fiscalCalendarRecId, FiscalCalendarYear::findByCalendarYearName(fiscalCalendarRecId, proj_FiscalCalendarYear.text()).StartDate).periodName());
    }
    if (!projectBudgetYear.text())
    {
        projectBudgetYear.text(FiscalCalendarYear::findYearByCalendarDate(fiscalCalendarRecId, DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone())).yearName());
    }
    element.updateDesign();
}

public void init()
{
    projYearEndProcess = element.args().caller();
    if (!projYearEndProcess)
    {
        throw error(Error::missingParameter(this));
    }

    super();

    projBudgetLine_ds.cacheCalculateMethod(tableMethodStr(ProjBudgetLine, displayActivityName));
    this.getValues();
    this.updateDesign();
}

public void retrieveProjSysQuery()
{
    SysQueryRun             queryForm;
    QueryBuildDataSource    fiscalPeriodQBDS;

    queryForm = new SysQueryRun(queryStr(ProjYearEndProcessQuery));

    if (queryForm.prompt())
    {
        yearEndProcessQuery = queryForm.query();

        fiscalPeriodQBDS = yearEndProcessQuery.dataSourceTable(tableNum(ProjBudgetAllocationLine)).addDataSource(tableNum(FiscalCalendarPeriod));
        fiscalPeriodQBDS.joinMode(JoinMode::NoExistsJoin);
        fiscalPeriodQBDS.addLink(fieldNum(ProjBudgetAllocationLine, FiscalCalendarPeriod),fieldNum(FiscalCalendarPeriod, RecId));

        element.setRanges();
    }
}

RunBase runBase()
{
    return projYearEndProcess;
}

public void setOpeningFiscalYear()
{
    FiscalYearName      nextFiscalYearName;
    FiscalCalendarRecId fiscalCalendarRecId = Ledger::fiscalCalendar(CompanyInfo::current());
    FiscalCalendarYear  projFiscalCalendarYear = FiscalCalendarYear::findByCalendarYearName(fiscalCalendarRecId, projectBudgetYear.text());

    date followingYearDate = DateTimeUtil::date(DateTimeUtil::addYears(DateTimeUtil::newDateTime(projFiscalCalendarYear.StartDate, 0), 1));
    nextFiscalYearName = FiscalCalendarYear::findYearByCalendarDate(fiscalCalendarRecId, followingYearDate).Name;

    if (nextFiscalYearName && ProjYearEndProcess::validateFiscalCalendarYear(nextFiscalYearName, false))
    {
        proj_FiscalCalendarYear.text(nextFiscalYearName);
        proj_FiscalCalendarPeriod.text(LedgerFiscalCalendar::findFirstOpenPeriodInYear(fiscalCalendarRecId, FiscalCalendarYear::findByCalendarYearName(fiscalCalendarRecId, proj_FiscalCalendarYear.text()).StartDate).periodName());
    }
}

public void setRanges()
{
    QueryBuildRange         qbrFromForecastModel;
    QueryBuildRange         qbrFiscalYearRecId;
    QueryBuildRange         qbrShowZero;
    RefRecId                fiscalYearRecId;

    qbrShowZero = SysQuery::findOrCreateRange(yearEndProcessQuery.dataSourceTable(tableNum(ProjBudgetAllocationLine)),
                                                                                    fieldNum(ProjBudgetAllocationLine, RecId));

    if (!showZeroRemainingChkBx.checked())
    {
        qbrShowZero.value((strFmt('((%1.%2 - %1.%3) != 0)',
                            tableStr(ProjBudgetAllocationLine),
                            fieldStr(ProjBudgetAllocationLine,TotalAllocationAmount),
                            fieldStr(ProjBudgetAllocationLine,ReductionAmount))));
    }
    else
    {
        yearEndProcessQuery.dataSourceTable(tableNum(ProjBudgetAllocationLine)).clearRange(fieldNum(ProjBudgetAllocationLine, RecId));
    }

    qbrFromForecastModel = SysQuery::findOrCreateRange(yearEndProcessQuery.dataSourceTable(tableNum(ProjBudget)),
                                                        fieldNum(ProjBudget, RemainingBudgetForecastModel));

    qbrFromForecastModel.value(SysQuery::value(fromForecastModelId.text()));

    fiscalYearRecId = FiscalCalendarYear::findByCalendarYearName(Ledger::fiscalCalendar(CompanyInfo::current()),projectBudgetYear.valueStr()).RecId;

    qbrFiscalYearRecId = SysQuery::findOrCreateRange(yearEndProcessQuery.dataSourceTable(tableNum(ProjBudgetAllocationLine)),
                                                        fieldNum(ProjBudgetAllocationLine, FiscalCalendarYear));

    qbrFiscalYearRecId.value((strFmt('((%1.%2 == %3) || (%1.%4 != 0))',
                                    tableStr(ProjBudgetAllocationLine),
                                    fieldStr(ProjBudgetAllocationLine,FiscalCalendarYear),
                                    SysQuery::value(fiscalYearRecId),
                                    fieldStr(ProjBudgetAllocationLine,FiscalCalendarPeriod))));

    qbrFiscalYearRecId = SysQuery::findOrCreateRange(yearEndProcessQuery.dataSourceTable(tableNum(FiscalCalendarPeriod)),
                                                        fieldNum(FiscalCalendarPeriod, FiscalCalendarYear));

    qbrFiscalYearRecId.value(SysQuery::valueNot(fiscalYearRecId));

    if (ProjCDSLockingOptionTable::get(ProjCDSLockingOption::ProjectBudget))
    {
        QueryBuildRange qbrExternal = SysQuery::findOrCreateRange(yearEndProcessQuery.dataSourceTable(tableNum(ProjTable)), 
                                                            fieldNum(ProjTable, ProjDataSource));
        qbrExternal.value(SysQuery::Value(0));
    }

    canExecuteQuery = true;
    projTable_ds.query(yearEndProcessQuery);
    projTable_ds.executeQuery();

    element.updateDesign();
}

private void setValues()
{
    projYearEndProcess.parmCarryForward(chkBoxCarryForward.checked());
    projYearEndProcess.parmAdjustGL(chkBoxGLbudget.checked());
    projYearEndProcess.parmFiscalCalendarID(fiscalCalendar.text());
    projYearEndProcess.parmFiscalYearName(proj_FiscalCalendarYear.text());
    projYearEndProcess.parmFiscalCalendarPeriod(proj_FiscalCalendarPeriod.text());
    projYearEndProcess.parmFromForecastModel(fromForecastModelId.text());
    projYearEndProcess.parmToForecastModel(toForecastModelId.text());
    projYearEndProcess.parmSalesCurrency(tansferSalesCurrencyChkBx.value());
    projYearEndProcess.parmShowZeroRemaining(showZeroRemainingChkBx.value());
    projYearEndProcess.parmProjFiscalYearName(projectBudgetYear.text());
    projYearEndProcess.parmIncludeProfitAndloss(includeProfitAndLossChkBx.value());
    projYearEndProcess.parmIncludeWIP(includeWIPChkBx.value());
    projYearEndProcess.parmIncludePayrollAllocation(includePayrollAllocationChkBx.value());
    xSysLastValue::saveLast(projYearEndProcess);
}

public void updateDesign()
{
    if (chkBoxCarryForward.checked())
    {
        chkBoxGLbudget.enabled(true);
        btnProcess.enabled(true);
    }
    else
    {
        chkBoxGLbudget.checked(false);
        chkBoxGLbudget.enabled(false);
        btnProcess.enabled(false);
    }

    if (!chkBoxGLbudget.checked())
    {
        proj_FiscalCalendarPeriod.enabled(false);
        toForecastModelId.enabled(false);
        toForecastModelId.mandatory(false);
        tansferSalesCurrencyChkBx.enabled(false);
        includeProfitAndLossChkBx.enabled(false);
        includeWIPChkBx.enabled(false);
        includePayrollAllocationChkBx.enabled(false);
    }
    else
    {
        proj_FiscalCalendarPeriod.enabled(true);
        toForecastModelId.enabled(true);
        toForecastModelId.mandatory(true);
        tansferSalesCurrencyChkBx.enabled(true);
        includeProfitAndLossChkBx.enabled(true);
        includeWIPChkBx.enabled(true);
        includePayrollAllocationChkBx.enabled(true);
    }

    if (projBudgetLine_ds.cursor())
    {
        btnViewProjectMenuItem.enabled(true);
        btnViewAccounts.enabled(true);
    }
    else
    {
        btnViewProjectMenuItem.enabled(false);
        btnViewAccounts.enabled(false);
    }
}

public display AmountMST remainingBudgetAmount(ProjBudgetAllocationLine  _projBudgetAllocationLine)
{
    AmountMST remainingBudgetAmount = (_projBudgetAllocationLine.TotalAllocationAmount - _projBudgetAllocationLine.ReductionAmount);

    return remainingBudgetAmount;
}

