public boolean modified()
{
    boolean ret;

    ret = super();

    element.setOpeningFiscalYear();

    ret = ProjYearEndProcess::modifiedFiscalCalendarYear(proj_FiscalCalendarYear.text(), this.text());

    if (ret && (yearEndProcessQuery &&
                this.text() != projYearEndProcess.parmProjFiscalYearName() &&
                Box::yesNo("@SYS327820", DialogButton::No) == DialogButton::Yes))
    {
        element.setRanges();
    }
    else
    {
        ret = (ret && yearEndProcessQuery == null);
    }

    if (ret)
    {
        projYearEndProcess.parmProjFiscalYearName(this.text());
    }
    else
    {
        this.text(projYearEndProcess.parmProjFiscalYearName());
    }

    return ret;
}

