void clicked()
{
    QueryBuildDataSource    fiscalPeriodQBDS;

    if (fromForecastModelId.text())
    {
        yearEndProcessQuery = new Query(queryStr(ProjYearEndProcessQuery));

        fiscalPeriodQBDS = yearEndProcessQuery.dataSourceTable(tableNum(ProjBudgetAllocationLine)).addDataSource(tableNum(FiscalCalendarPeriod));
        fiscalPeriodQBDS.joinMode(JoinMode::NoExistsJoin);
        fiscalPeriodQBDS.addLink(fieldNum(ProjBudgetAllocationLine, FiscalCalendarPeriod),fieldNum(FiscalCalendarPeriod, RecId));

        element.setRanges();
    }
    else
    {
        checkFailed("@SYS328438");
    }
}

