Method.Source:
using Microsoft.Dynamics.AX.Framework.Utilities.UrlHelper;

[Form]
public class PurchAgreement extends FormRun
    implements AgreementIRereadableAgreementForm, OfficeIGeneratedWorkbookCustomExporter, OfficeITemplateCustomExporter
{
    const str CustomExportLinesToExcelOptionId = 'LinesCustom';
    const str ExportLineFulfillmentsToExcelOptionId = 'LineFulfillmentsCustom';
    const str ExportLineNotesToExcelOptionId = 'LineNotesCustom';

    boolean                             isCalledFromLP;
    PurchAgreementForm                  purchAgreementForm;
    PurchAgreementFormDatasourceManager purchAgreementFormDatasourceManager;
    AgreementFormArgs                   purchAgreementFormArgs;

    InventDimCtrl_Frm_AgreementLine     inventDimFormSetup;

    LogisticsLocationRecId              postalAddressLocation;
    // <GEERU>
    #ISOCountryRegionCodes
    boolean                             countryRegion_RU;
    // </GEERU>

    boolean     isFormInEditMode;
    boolean     hasActiveWorkflow;

    AgreementClassificationLookup       agreementClassificationLookup;
    InventDistinctProduct               productFromLookup;

    private DataAreaId startupDataAreaId;
    private DataAreaId currentCompany;

}

public void activate(boolean _active)
{
    super(_active);

    if (_active)
    {
        element.changeOpenCompany(currentCompany);
    }
}

/// <summary>
/// Checks if the form can submit to workflow.
/// </summary>
/// <returns>
/// true if an purchase agreement can be submitted to workflow; otherwise, false.
/// </returns>
/// <remarks>
/// The workflow yellow bar and submit button appears only if this method returns true.
/// </remarks>
public boolean canSubmitToWorkflow()
{
    if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector))
        && element.design().workflowDatasource() == purchAgreementHeader_ds.id())
    {
        return purchAgreementHeader.canSubmitToWorkflow();
    }
    else
    {
        return super();
    }
}

private void changeOpenCompany(DataAreaId _companyDataAreaId)
{
    if (_companyDataAreaId != curExt())
    {
        appl.setDefaultCompany(_companyDataAreaId, false);
    }
}

public void close()
{
    super();

    element.changeOpenCompany(startupDataAreaId);
}

/// <summary>
/// Creates a record in the specific data source.
/// </summary>
/// <param name="_formDataSourceName">
/// Name of the data source where the record will be created.
/// </param>
/// <param name="_append">
/// A Boolean that indicates whether to insert the record after or before the current
/// cursor position. If true, the new record is inserted after the current record; otherwise
/// before the current record.
/// </param>
/// <remarks>
/// If the current data source is the agreement line super type the correct sub types is created.
/// </remarks>
public void createRecord(str        _formDataSourceName,
                         boolean    _append = true)
{
    if (_formDataSourceName == aLine_ds.name())
    {
        Map tableNamesMap = new Map(Types::String, Types::String);
        TableId tableId = AgreementLine::getTableIdFromCommitmentType(PurchAgreementHeader.DefaultAgreementLineType);
        tableNamesMap.insert(_formDataSourceName, tableId2Name(tableId));
        aLine_ds.createTypes(tableNamesMap, _append);
    }
    else
    {
        super(_formDataSourceName, _append);
    }
}

public void disableButtons()
{
    purchAgreementForm.disableHeaderButtons();
    purchAgreementForm.disableLineButtons();
}

/// <summary>
/// Gets the record for which the Document Handling should be attached.
/// </summary>
/// <returns>
/// The Purchase Agreement Header where the Document Handling is attached.
/// </returns>
public Common docCursor()
{
    Common docCursor;

    docCursor = super();

    switch (docCursor.TableId)
    {
        case tableNum(AgreementHeader),
             tableNum(AgreementHeaderDefault),
             tableNum(PurchAgreementHeaderDefault),
             tableNum(AgreementHeaderDefault),
             tableNum(AgreementClassification),
             tableNum(LogisticsLocation),
             tableNum(LogisticsPostalAddress) :

            docCursor = purchAgreementHeader;
            break;

        case tableNum(AgreementLineDefault),
             tableNum(AgreementLineQuantityCommitment),
             tableNum(AgreementLineVolumeCommitment),
             tableNum(InventDim) :

            docCursor = aLine;
            break;
    }

    return docCursor;
}

public void enableButtons()
{
    if (purchAgreementHeader.RecId)
    {
        purchAgreementForm.enableHeaderButtons(purchAgreementHeader);
        purchAgreementForm.enableLineButtons(aLine, purchAgreementHeader);
        purchAgreementForm.enableHeaderFields(purchAgreementHeader_ds);
    }
    else
    {
        this.disableButtons();
    }
}

internal void lockCLMOwnedHeaderFields()
{
    this.enableCLMOwnedHeaderFields(false);
}

internal void unlockCLMOwnedHeaderFields()
{
    this.enableCLMOwnedHeaderFields(true);
}

protected void enableCLMOwnedHeaderFields(boolean _isEditable)
{
    int i;
    FieldId fieldId;

    container fields = CLMIntegrationPurchAgreementDataProvider::getCLMIntegrationPurchAgreementHeaderFields();        
    for (i = 1; i <= conLen(fields); i++)
    {
        fieldId = conPeek(fields, i);
        purchAgreementHeader_ds.object(fieldId).allowEdit(_isEditable);
    }

    fields = CLMIntegrationPurchAgreementDataProvider::getCLMIntegrationPurchAgreementHeaderDefaultFields();
    for (i = 1; i <= conLen(fields); i++)
    {
        fieldId = conPeek(fields, i);
        purchAgreementHeaderDefault_ds.object(fieldId).allowEdit(_isEditable);
    }

    fields = CLMIntegrationPurchAgreementDataProvider::getCLMIntegrationAgreementHeaderDefaultFields();
    for (i = 1; i <= conLen(fields); i++)
    {
        fieldId = conPeek(fields, i);
        agreementHeaderDefault_ds.object(fieldId).allowEdit(_isEditable);
    }

    // Contact
    editContactPersonName.allowEdit(_isEditable);
}

internal void lockCLMOwnedLineFields()
{
    this.enableCLMOwnedLineFields(false);
}

internal void unlockCLMOwnedLineFields()
{
    this.enableCLMOwnedLineFields(true);
}

protected void enableCLMOwnedLineFields(boolean _isEditable)
{
    int i;
    FieldId fieldId;

    container fields = CLMIntegrationPurchAgreementDataProvider::getCLMIntegrationAgreementLineFields();
    for (i = 1; i <= conLen(fields); i++)
    {
        fieldId = conPeek(fields, i);
        aLine_ds.object(fieldId).allowEdit(_isEditable);
    }

    fields = CLMIntegrationPurchAgreementDataProvider::getCLMIntegrationAgreementLineQuantityCommitmentFields();
    for (i = 1; i <= conLen(fields); i++)
    {
        fieldId = conPeek(fields, i);
        aLine_AgreementLineQuantityCommitment_ds.object(fieldId).allowEdit(_isEditable);
    }

    fields = CLMIntegrationPurchAgreementDataProvider::getCLMIntegrationAgreementLineVolumeCommitmentFields();
    for (i = 1; i <= conLen(fields); i++)
    {
        fieldId = conPeek(fields, i);
        ALine_AgreementLineVolumeCommitment_ds.object(fieldId).allowEdit(_isEditable);
    }

    // Net Amount
    NetAmount.allowEdit(_isEditable);

    // Site, Warehouse
    inventDim_ds.allowEdit(_isEditable);
}

/// <summary>
/// Disables fields if transportation template has been set.
/// </summary>
public void enableTMSFields()
{
    TMSTransportationTemplateId transTempId;
    TMSCarrierCode              carrierCode;
    TMSCarrierGroupCode         carrierGroupCode;
    TMSModeCode                 modeCode;
    TMSRouteConfigCode          routeConfigCode;
    boolean                     modeCodeEnabled;

    transTempId         = tmsPurchAgreementHeader.TransportationTemplateId;
    carrierCode         = tmsPurchAgreementHeader.CarrierCode;
    carrierGroupCode    = tmsPurchAgreementHeader.CarrierGroupCode;
    modeCode            = tmsPurchAgreementHeader.ModeCode;
    routeConfigCode     = tmsPurchAgreementHeader.RouteConfigCode;
    modeCodeEnabled     = tmsPurchAgreementHeader.ModeCode != '' && tmsPurchAgreementHeader.CarrierCode == '';

    tmsPurchAgreementHeader_ds.object(fieldNum(TMSPurchAgreementHeader, ModeCode)).allowEdit(!(carrierCode || transTempId || carrierGroupCode || routeConfigCode));
    tmsPurchAgreementHeader_ds.object(fieldNum(TMSPurchAgreementHeader, CarrierCode)).allowEdit(!(transTempId ||carrierGroupCode || modeCodeEnabled || routeConfigCode));
    tmsPurchAgreementHeader_ds.object(fieldNum(TMSPurchAgreementHeader, CarrierServiceCode)).allowEdit(!(transTempId || carrierGroupCode || modeCodeEnabled || routeConfigCode));
    tmsPurchAgreementHeader_ds.object(fieldNum(TMSPurchAgreementHeader, CarrierGroupCode)).allowEdit(!(transTempId || carrierCode || routeConfigCode || modeCode));
    tmsPurchAgreementHeader_ds.object(fieldNum(TMSPurchAgreementHeader, RouteConfigCode)).allowEdit(!(carrierCode || transTempId || modeCode || carrierGroupCode));
    tmsPurchAgreementHeader_ds.object(fieldNum(TMSPurchAgreementHeader, TransportationTemplateId)).allowEdit(!(carrierCode || carrierGroupCode || modeCode || routeConfigCode));
}

/// <summary>
/// Builds the shared datasource manager between this form and <see cref="PurchAgreementCreate Form"/>.
/// </summary>
void buildSharedDatasourceManager()
{
    // Init of the shared code object.
    purchAgreementFormDatasourceManager = PurchAgreementFormDatasourceManager::construct();
    purchAgreementFormDatasourceManager.parmFormRun(element);
    purchAgreementFormDatasourceManager.parmAgreementHeader(purchAgreementHeader);
    purchAgreementFormDatasourceManager.parmPurchSalesAgreementHeaderDefault(purchAgreementHeaderDefault);
    purchAgreementFormDatasourceManager.parmAgreementHeaderDefault(agreementHeaderDefault);
    purchAgreementFormDatasourceManager.parmAgreementHeaderExt_RU(purchAgreementHeaderExt_RU);
    purchAgreementFormDatasourceManager.parmTmsAgreementHeader(tmsPurchAgreementHeader);
}

private void applyCLMIntegrationContractRange(Query _query, CLMIntegrationContractTable _contract)
{
    QueryBuildDataSource queryBuildDataSource = SysQuery::findOrCreateDataSource(_query, tableNum(PurchAgreementHeader));

    List allRelationDocuments = CLMIntegrationContractDocumentRelationship::findAllByContract(_contract.RecId, tableNum(PurchAgreementHeader));

    ListEnumerator le = allRelationDocuments.getEnumerator();
    while(le.moveNext())
    {
        CLMIntegrationContractDocumentRelationship document = le.current();
        queryBuildDataSource.addRange(fieldNum(PurchAgreementHeader,RecId)).value(SysQuery::value(document.DocumentRefRecId));
    }
}

/// <summary>
/// Applies the range on the <c>PurchAgreementHeader</c> table for the <c>PurchNumberSequence</c> field.
/// </summary>
/// <param name="_query">
/// The input query.
/// </param>
/// <param name="_purchRFQTable">
/// The <c>PurchRFQTable</c> table.
/// </param>
/// <remarks>
/// This method is called when initializing the query to filters the <c>PurchAgreementHeader</c> table
/// with the required <c>PurchNumberSequence</c> values.
/// </remarks>
private void applyPurchRFQRange(Query _query, PurchRFQTable _purchRFQTable)
{
    PurchRFQLine            purchRFQLine;
    QueryBuildDataSource    queryBuildDataSource;
    QueryBuildRange         queryBuildRange;

    queryBuildDataSource = SysQuery::findOrCreateDataSource(_query, tableNum(PurchAgreementHeader));
    queryBuildDataSource.clearDynalinks();

    while select AgreementId from purchRFQLine
        group by AgreementId
        where purchRFQLine.rfqId == _purchRFQTable.rfqId
    {
        queryBuildRange = queryBuildDataSource.addRange(fieldNum(PurchAgreementHeader, PurchNumberSequence));
        queryBuildRange.value(queryValue(purchRFQLine.AgreementId));
    }

    queryBuildRange.status(RangeStatus::Locked);
}

/// <summary>
/// Applies the range on the <c>AgreementHeaderDefault</c> table for the field depending on the calling record.
/// </summary>
/// <param name = "_query">
/// The input query.
/// </param>
/// <param name = "_callerRecord">
/// The calling record.
/// </param>
/// <remarks>
/// This method is called when initializing the query to filter the <c>AgreementHeaderDefault</c> table.
/// </remarks>
protected void applyQueryRangeByCallerRecord(Query _query, Common _callerRecord)
{
    if (_query && _callerRecord)
    {
        QueryBuildDataSource qbdsAgreementHeaderDefault;

        // If the caller is Projects list page or Projects details form
        if (_callerRecord.TableId == tableNum(ProjTable))
        {
            ProjTable projTable = _callerRecord;
            qbdsAgreementHeaderDefault = _query.dataSourceTable(tablenum(AgreementHeaderDefault));
            qbdsAgreementHeaderDefault.addRange(fieldNum(AgreementHeaderDefault,Project)).value(queryValue(projTable.ProjId));
            qbdsAgreementHeaderDefault.joinMode(JoinMode::InnerJoin);
        }
        else if (_callerRecord.TableId == tableNum(ProjInvoiceTable))
        {
            ProjInvoiceTable projInvoiceTable = _callerRecord;
            qbdsAgreementHeaderDefault = _query.dataSourceTable(tablenum(AgreementHeaderDefault));
            QueryBuildDataSource qbds = qbdsAgreementHeaderDefault.addDataSource(tableNum(ProjTable));
            qbds.addLink(fieldNum(AgreementHeaderDefault,Project), fieldNum(ProjTable, ProjId));
            qbds.joinMode(JoinMode::ExistsJoin);
            qbds.addRange(fieldNum(ProjTable,ProjInvoiceProjId)).value(queryValue(projInvoiceTable.ProjInvoiceProjId));
        }
    }
}

/// <summary>
/// Applies the range on the <c>PurchAgreementHeader</c> table for the <c>IsDeleted</c> and <c>VendorDataAreaId</c> fields.
/// </summary>
/// <param name = "_query">
/// The input query.
/// </param>
protected void applyPurchAgreementHeaderRange(Query _query)
{
    if (_query)
    {
        QueryBuildDataSource qbds = _query.dataSourceTable(tableNum(PurchAgreementHeader));
        qbds.addSortField(fieldNum(PurchAgreementHeader, PurchNumberSequence));

        QueryBuildRange qbr = qbds.addRange(fieldNum(PurchAgreementHeader,IsDeleted));
        qbr.value(queryValue(NoYes::No));

        // <GEERU>
        if (!SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]))
        {
            // </GEERU>
            qbr.status(RangeStatus::Hidden);
            // <GEERU>
        }
        // </GEERU>
        qbr = qbds.addRange(fieldNum(PurchAgreementHeader, VendorDataAreaId));
        qbr.value(queryValue(appl.company().dataArea(tableNum(VendTable))));
        qbr.status(RangeStatus::Hidden);
    }
}

    /// <summary>
    /// Applies the appropriate ranges to query depending on the where the <c>PurchAgreement</c> form is opened from.
    /// </summary>
    private void applyQueryRanges()
    {
        if (element.args())
        {
            Query  query = purchAgreementHeader_ds.query();
            Common callerRecord = element.args().record();

            if (callerRecord)
            {
				if (element.args().menuItemName() ==  menuitemDisplayStr(PurchAgreementListPage) &&
					callerRecord.TableId == tableNum(PurchRFQTable))
				{
					this.applyPurchRFQRange(query, callerRecord);
				}
                if (element.args().menuItemName() ==  menuitemDisplayStr(PurchAgreementListPage) &&
                    callerRecord.TableId == tableNum(CLMIntegrationContractTable))
                {
                    this.applyCLMIntegrationContractRange(query, callerRecord);
                }
				else
				{
                    this.applyPurchAgreementHeaderRange(query);
					this.applyQueryRangeByCallerRecord(query, callerRecord);
				}
            } 
			else
            {
				this.applyPurchAgreementHeaderRange(query);
            }
        }
    }

private PurchAgreementHeader findRecordFromRequestQueryParameter(RequestQueryParameter _requestQueryParameter)
{
    PurchAgreementHeader purchAgreementHeaderLocal;

    for (int i = 0; i < _requestQueryParameter.GetFieldValueCount(); i++)
    {
        var fieldName = _requestQueryParameter.GetField(i);
        var fieldValue = _requestQueryParameter.GetValue(i);

        if (fieldValue && fieldName == fieldStr(PurchAgreementHeader, RecId))
        {
            purchAgreementHeaderLocal = PurchAgreementHeader::find(any2Int64(fieldValue));
            break;
        }
    }

    return purchAgreementHeaderLocal;
}

private PurchAgreementHeader getPurchAgreementFromURLQuery(str _queryValue)
{
    PurchAgreementHeader purchAgreementHeaderLocal;

    if (_queryValue)
    {
        UrlGenerator generator = new UrlGenerator();
        generator.ParseRequestQueryParameter(_queryValue);

        RequestQueryParameterCollection parameterCollection = generator.RequestQueryParameterCollection;

        while (parameterCollection.MoveNext())
        {
            RequestQueryParameter parameter = parameterCollection.Current();

            if (parameter.DataSource == tableStr(PurchAgreementHeader))
            {
                purchAgreementHeaderLocal = this.findRecordFromRequestQueryParameter(parameter);

                if (purchAgreementHeaderLocal)
                {
                    break;
                }
            }
        }
    }

    return purchAgreementHeaderLocal;
}

/// <summary>
/// Initializes the Purchase Agreement form and creates a new instance of the Purchase Agreement Form class.
/// </summary>
public void init()
{
    #ISOCountryRegionCodes
    boolean isFrenchRegulatoryEnabled = PublicSectorUtils::isFrenchRegulatoryEnabled();
    boolean isBudgetReservationsEnabled = PublicSectorUtils::isBudgetReservationEnabled();
    startupDataAreaId = curExt();
    currentCompany = curExt();

    Args args = element.args();

    if (args)
    {
        args.copyCallerQuery(CopyCallerQuery::No);

        if (args.menuItemName() == menuItemDisplayStr(PurchAgreementDetails))
        {
            str queryValue;

            queryValue = getClientURLQueryValue('q');

            // In case we have a query from URL and the form is opened from the <c>PurchAgreementDetails</c> menu 
            // we should apply company from it otherwise, we should check records from args
            if (queryValue)
            {
                currentCompany = CompanyInfo::find(false, this.getPurchAgreementFromURLQuery(queryValue).BuyingLegalEntity).DataArea;

                element.changeOpenCompany(currentCompany);
            }


            if (!queryValue
                && args.record()
                && args.dataset() == tableNum(PurchAgreementHeader))
            {
                PurchAgreementHeader purchAgreementHeaderArgument = element.args().record();
                currentCompany = CompanyInfo::find(false, purchAgreementHeaderArgument.BuyingLegalEntity).DataArea;

                element.changeOpenCompany(currentCompany);
            }
        }
    }

    super();

    if (element.args() && element.args().menuItemName() ==  menuitemDisplayStr(PurchAgreementListPage))
    {
        this.applyQueryRanges();
    }

    Query query = purchAgreementHeader_ds.query();
    QueryBuildDataSource qbds = query.dataSourceTable(tableNum(PurchAgreementHeader));
    query.addQueryFilter(qbds, fieldStr(PurchAgreementHeader, PurchNumberSequence));
    query.addQueryFilter(qbds, fieldStr(PurchAgreementHeader, VendAccount));

    // Init of the shared code object.
    element.buildSharedDatasourceManager();

    // Init of dependency objects.
    agreementClassificationLookup = AgreementClassificationLookup::newAgreementClassificationLookup(tablenum(PurchAgreementHeader));

    purchAgreementForm = PurchAgreementForm::newFormRun(element);
    purchAgreementForm.getSysLastValue();
    purchAgreementForm.parmHeaderDefaultDimensionEntryControl(DimensionEntryControlHeader);

    purchAgreementForm.initRangesFromCaller(purchAgreementHeader_ds, agreementHeaderDefault_ds);

    // Creation of the PurchAgreementArgs DTO.
    purchAgreementFormArgs = AgreementFormArgs::construct();
    purchAgreementForm.parmPurchAgreementFormArgs(purchAgreementFormArgs);

    if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
    {
        purchAgreementHeaderDefault_ds.object(fieldNum(PurchAgreementHeaderDefault, VendorInvoiceAccount)).mandatory(NoYes::Yes);
        purchAgreementForm.updateDesign_PSN(purchAgreementHeader_ds, ALine_DS);

        purchAgreementHeader_PurchAgreementType1.visible(isFrenchRegulatoryEnabled);
        changeContractor_PSN.visible(isFrenchRegulatoryEnabled);
        currentAmount_PSN.visible(isFrenchRegulatoryEnabled);
        purchAgreementChild_PSN.visible(isFrenchRegulatoryEnabled);
        purchAgreementFinDimensionAccess_PSN.visible(isFrenchRegulatoryEnabled);
        purchAgreementTree_PSN.visible(isFrenchRegulatoryEnabled);
        vendBankAccount_PSN.visible(isFrenchRegulatoryEnabled);
        tabAdministration_PSN.visible(isFrenchRegulatoryEnabled);

        hasActiveWorkflow = WorkflowTable::findActiveTemplate(workFlowTypeStr(PurchAgreementType)).RecId;
    }
    else
    {
        purchAgreementHeaderDefault_ds.object(fieldNum(PurchAgreementHeaderDefault, VendorInvoiceAccount)).mandatory(NoYes::No);
    }

    element.updateDesign(InventDimFormDesignUpdate::Init);

    element.setTitleText(true);
    logisticsPostalAddress_ds.addFieldToSelectionList(fieldNum(LogisticsPostalAddress, CountryRegionId));
    // <GEERU>
    countryRegion_RU = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]);
    // </GEERU>

    BudgetReservationLine_PSN.visible(isBudgetReservationsEnabled);
    BudgetReservationLineAgreementLineMenu_PSN.visible(isBudgetReservationsEnabled);

    // External contract
    TabHeaderExternalContract.visible(CLMIntegrationFeature::isEnabled());
    TabExternalContract.visible(CLMIntegrationFeature::isEnabled());

    // <GTE>
    if (TaxSolutionScopeIntegrationUtil::isCompanyEnabled())
    {
        TaxDocumentLauncher.visible(true);
    }

    if (TaxIntegrationUtils::isTaxInformationEnabled())
    {
        TransTaxInformation.visible(true);
    }
    // </GTE>

    if (PurchIsSupplierShipmentDatesControlsVisibleFlight::instance().isEnabled())
    {
        boolean isSupplierShipmentDatesEnabled = PurchParameters::find().IsSupplierShipmentDatesEnabled;
        ShipCalendarId.visible(isSupplierShipmentDatesEnabled);
        PurchAgreementHeader_ShipCalendarId.visible(isSupplierShipmentDatesEnabled);
    }
}

