}

/// <summary>
/// Sets proper title for the Purchase Agreement form.
/// </summary>
/// <param name="_setInitialText">
/// A boolean value indicating whether the initial purchase agreement text should be used as title.
/// </param>
public void setTitleText(boolean _setInitialText = false)
{
    if (_setInitialText)
    {
        headerTitle.text("@SYS311165");
    }
    else if (purchAgreementHeader.VendAccount)
    {
        headerTitle.text(strFmt('%1 : %2 - %3',
                                purchAgreementHeader.PurchNumberSequence,
                                purchAgreementHeader.VendAccount,
                                purchAgreementHeader.vendName()));
    }
    else
    {
        headerTitle.text(strFmt('%1', "@SYS328765"));
    }
}

/// <summary>
/// Updated purchase agreement after transportation fields are changed
/// </summary>
public void tmsUpdatePurchAgreement()
{
    agreementHeaderDefault.DeliveryMode = TMSCarrierService::findDlvModeId(tmsPurchAgreementHeader.CarrierCode, tmsPurchAgreementHeader.CarrierServiceCode);

    agreementHeaderDefault_ds.refresh();
}

/// <summary>
/// Updates form design elements.
/// </summary>
/// <param name="_mode">
/// Mode describing purpose for the call.
/// </param>
/// <exception cref="Exception::Error">
/// Throws when called with non-supported mode.
/// </exception>
public void updateDesign(InventDimFormDesignUpdate _mode)
{
    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init:
            if (!inventDimFormSetup)
            {
                inventDimFormSetup = InventDimCtrl_Frm_AgreementLine::newFromForm(element, fieldNum(InventDimSetupGrid, ShowGridPurchLine));
            }

        case InventDimFormDesignUpdate::Active      :
            inventDimFormSetup.setIsCategoryBased(aLine.isCategoryBased());
            inventDimFormSetup.formActiveSetup(InventDimGroupSetup::newItemId(aLine.ItemId));
            inventDimFormSetup.formSetControls(true);
            break;

        case InventDimFormDesignUpdate::FieldChange :
            inventDimFormSetup.setIsCategoryBased(aLine.isCategoryBased());
            inventDimFormSetup.formActiveSetup(InventDimGroupSetup::newItemId(aLine.ItemId));
            inventDim.clearNotSelectedDim(inventDimFormSetup.parmDimParmEnabled());
            inventDimFormSetup.formSetControls(false);
            break;

        default :
            throw error(strFmt("@SYS54195",funcName()));
    }
}

public void updateFollowUpFields()
{
    #AgreementsRemains
    container fulfillmentValues;

    // Query all fulfillment values at once...
    fulfillmentValues += AgreementLine::calculateFulfillmentForLine(aLine);

    // Distributing them among fulfillment fields.
    agreementDelivered.realValue(        conPeek(fulfillmentValues, #DeliveredNotInvoicedIndex));
    agreementInvoiced.realValue(         conPeek(fulfillmentValues, #InvoicedIndex));
    agreementRemainingRelease.realValue( conPeek(fulfillmentValues, #RemainingIndex));
    agreementReleased.realValue(         conPeek(fulfillmentValues, #ReleasedNotInvoicedNorDeliveredIndex));

    pdsCWAgreementDelivered.realValue(        conPeek(fulfillmentValues, #PdsCWDeliveredNotInvoicedIndex));
    pdsCWAgreementInvoiced.realValue(         conPeek(fulfillmentValues, #PdsCWInvoicedIndex));
    pdsCWAgreementRemainingRelease.realValue( conPeek(fulfillmentValues, #PdsCWRemainingIndex));
    pdsCWAgreementReleased.realValue(         conPeek(fulfillmentValues, #PdsCWReleasedNotInvoicedNorDeliveredIndex));
}

public void customizeMenuOptions(OfficeMenuOptions _menuOptions)
{
    List exportOptions = _menuOptions.customMenuItems();

    var exportOption = OfficeGeneratedExportMenuItem::construct(tableStr(PurchPurchaseAgreementLineV2Entity), CustomExportLinesToExcelOptionId);
    exportOption.displayName("@SCM:PurchaseAgreementHeaderAndLines");
    exportOptions.addEnd(exportOption);

    var exportOptionFulfillment = OfficeGeneratedExportMenuItem::construct(tableStr(PurchPurchaseAgreementLineFulfillmentEntity), ExportLineFulfillmentsToExcelOptionId);
    exportOptionFulfillment.displayName("@SCM:PurchPurchaseAgreementLineFulfillmentEntity");
    exportOptions.addEnd(exportOptionFulfillment);

    var exportOptionNote = OfficeGeneratedExportMenuItem::construct(tableStr(PurchaseAgreementLineNoteEntity), ExportLineNotesToExcelOptionId);
    exportOptionNote.displayName("@SCM:PurchaseAgreementLineNoteEntityLabel");
    exportOptions.addEnd(exportOptionNote);
}

public ExportToExcelDataEntityContext getDataEntityContext(OfficeGeneratedExportMenuItem _menuItem)
{
    ExportToExcelDataEntityContext context = null;

    switch (_menuItem.id())
    {
        case CustomExportLinesToExcelOptionId:
            context = ExportToExcelDataEntityContext::constructDefault(dataentityviewstr(PurchPurchaseAgreementHeaderV2Entity));

            var headerEntityInfo = context.findEntityByName(dataentityviewstr(PurchPurchaseAgreementHeaderV2Entity));
            headerEntityInfo.filter(this.createFilterWithCompany(
                headerEntityInfo.entityName(),
                fieldStr(PurchPurchaseAgreementHeaderV2Entity, PurchaseAgreementId),
                PurchAgreementHeader.PurchNumberSequence,
                this.viewOptionHelper().getViewOption() == FormViewOption::Grid));

            var lineEntityInfo = context.addEntityDefault(dataentityviewstr(PurchPurchaseAgreementLineV2Entity));
            lineEntityInfo.filter(this.createFilterWithCompany(
                lineEntityInfo.entityName(),
                fieldStr(PurchPurchaseAgreementLineV2Entity, PurchaseAgreementId),
                PurchAgreementHeader.PurchNumberSequence,
                this.viewOptionHelper().getViewOption() == FormViewOption::Grid));
            break;

        case ExportLineFulfillmentsToExcelOptionId:
            context = ExportToExcelDataEntityContext::constructDefault(dataentityviewstr(PurchPurchaseAgreementLineFulfillmentEntity));

            var fulfillmentEntityInfo = context.findEntityByName(dataentityviewstr(PurchPurchaseAgreementLineFulfillmentEntity));
            fulfillmentEntityInfo.filter(this.createFilterWithCompany(
                fulfillmentEntityInfo.entityName(),
                fieldStr(PurchPurchaseAgreementLineFulfillmentEntity, PurchaseAgreementId),
                PurchAgreementHeader.PurchNumberSequence,
                this.viewOptionHelper().getViewOption() == FormViewOption::Grid));
            break;

        case ExportLineNotesToExcelOptionId:                
            context = ExportToExcelDataEntityContext::constructDefault(dataentityviewstr(PurchaseAgreementLineNoteEntity));

            var noteEntityInfo = context.findEntityByName(dataentityviewstr(PurchaseAgreementLineNoteEntity));
            noteEntityInfo.filter(this.createFilterWithCompany(
                noteEntityInfo.entityName(),
                fieldStr(PurchaseAgreementLineNoteEntity, PurchaseAgreementId),
                PurchAgreementHeader.PurchNumberSequence,
                this.viewOptionHelper().getViewOption() == FormViewOption::Grid));
            break;
    }

    return context;
}

public Map getInitialTemplateFilters(OfficeTemplateExportMenuItem _menuItem) { Map filtersToApply = new Map(Types::String, Types::Class);
switch (_menuItem.dataEntityName()) { case dataentityviewstr(PurchPurchaseAgreementHeaderV2Entity): case dataentityviewstr(PurchPurchaseAgreementLineV2Entity): case dataentityviewstr(PurchPurchaseAgreementLineFulfillmentEntity): case dataentityviewstr(PurchaseAgreementLineNoteEntity):
filtersToApply.insert( dataentityviewstr(PurchPurchaseAgreementHeaderV2Entity), this.createFilterWithCompany( dataentityviewstr(PurchPurchaseAgreementHeaderV2Entity), fieldStr(PurchPurchaseAgreementHeaderV2Entity, PurchaseAgreementId), PurchAgreementHeader.PurchNumberSequence, this.viewOptionHelper().getViewOption() == FormViewOption::Grid));
filtersToApply.insert( dataentityviewstr(PurchPurchaseAgreementLineV2Entity), this.createFilterWithCompany( dataentityviewstr(PurchPurchaseAgreementLineV2Entity), fieldStr(PurchPurchaseAgreementLineV2Entity, PurchaseAgreementId), PurchAgreementHeader.PurchNumberSequence, this.viewOptionHelper().getViewOption() == FormViewOption::Grid));
filtersToApply.insert( dataentityviewstr(PurchPurchaseAgreementLineFulfillmentEntity), this.createFilterWithCompany( dataentityviewstr(PurchPurchaseAgreementLineFulfillmentEntity), fieldStr(PurchPurchaseAgreementLineFulfillmentEntity, PurchaseAgreementId), PurchAgreementHeader.PurchNumberSequence, this.viewOptionHelper().getViewOption() == FormViewOption::Grid));
filtersToApply.insert( dataentityviewstr(PurchaseAgreementLineNoteEntity), this.createFilterWithCompany( dataentityviewstr(PurchaseAgreementLineNoteEntity), fieldStr(PurchaseAgreementLineNoteEntity, PurchaseAgreementId), PurchAgreementHeader.PurchNumberSequence, this.viewOptionHelper().getViewOption() == FormViewOption::Grid)); break; }
return filtersToApply; }

public void updateTemplateSettings(OfficeTemplateExportMenuItem _menuItem, Microsoft.Dynamics.Platform.Integration.Office.SettingsManager _settingsManager)
{
    // No updates to the template settings are required.
}

Microsoft.Dynamics.Platform.Integration.Office.FilterCollectionNode createFilterWithCompany(str _entityName, str _fieldStr, anytype _fieldValue, boolean _skipRecordFilter = false)
{
    ExportToExcelFilterTreeBuilder filterBuilder = new ExportToExcelFilterTreeBuilder(_entityName);
    Microsoft.Dynamics.Platform.Integration.Office.FilterNode filter = null;
    if (!_skipRecordFilter)
    {
        filter = filterBuilder.areEqual(_fieldStr, _fieldValue);
    }
    return filterBuilder.and(filterBuilder.companyFilter(), filter);
}

