/// <summary>
/// Initializing the form data source Purchase Agreement Header.
/// </summary>
/// <remarks>
/// Setting Purchase Agreement Header record sorting to follow the Agreement ID and filters out
/// deleted records.
/// </remarks>
public void init()
{
    QueryBuildDataSource qbds;
    QueryBuildRange      qbr;
    super();

    qbds = this.query().dataSourceTable(tableNum(PurchAgreementHeader));

    qbr = qbds.addRange(fieldNum(PurchAgreementHeader, IsDeleted));
    qbr.value(queryValue(NoYes::No));
    qbr.status(RangeStatus::Hidden);

    qbr = qbds.addRange(fieldNum(PurchAgreementHeader, BuyingLegalEntity));
    qbr.value(queryValue(CompanyInfo::current()));
    qbr.status(RangeStatus::Hidden);

    // Enable caching of the document handling...
    purchAgreementHeader_ds.cacheAddMethod(tableMethodStr(PurchAgreementHeader,showDocHandIcon));

    logisticsPostalAddress_ds.validTimeStateUpdate(ValidTimeStateUpdate::Correction);

    logisticsPostalAddress_ds.query().validTimeStateDateTimeRange(DateTimeUtil::minValue(),
                                                                  DateTimeUtil::maxValue());
}

/// <summary>
/// Enabling or disabling form buttons based on the active record.
/// </summary>
/// <returns>
/// Result of the super call.
/// </returns>
public int active()
{
    int ret;

    AgreementClassification agreementClassification;

    ret = super();

    if (purchAgreementForm.parmIsIntegrationOwnerCLM() 
        && !purchAgreementHeader.isAgreementCLMOwned())
    {
        purchAgreementForm.parmIsIntegrationOwnerCLM(false);
        element.unlockCLMOwnedHeaderFields();
        element.unlockCLMOwnedLineFields();
    }
    else if (!purchAgreementForm.parmIsIntegrationOwnerCLM()
        && purchAgreementHeader.isAgreementCLMOwned())
    {
        purchAgreementForm.parmIsIntegrationOwnerCLM(true);
    }

    if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
    {
        element.enableButtons();
    }

    element.setTitleText();
    purchAgreementForm.enableHeaderFields(purchAgreementHeader_ds);
    purchAgreementForm.enableProjectHeaderFieldAccess(purchAgreementHeader_ds, agreementHeaderDefault_ds);

    // <GEERU>
    if (countryRegion_RU)
    {
        purchAgreementForm.enableHeaderFields_RU(purchAgreementHeader_ds, purchAgreementHeaderExt_RU_ds, purchAgreementHeaderDefault_ds);
    }
    // </GEERU>

    if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
    {
        agreementClassification = AgreementClassification::find(purchAgreementHeader.AgreementClassification);
        tabSubcontractor.visible(agreementClassification.Subcontractor_PSN);
        tabCertification.visible(agreementClassification.Certification_PSN);
        purchAgreementActivitiesSummary.enabled(agreementClassification.Activity_PSN);

        if (ret && purchAgreementHeader.WorkflowStatus_PSN && hasActiveWorkflow)
        {
            element.enableButtons();
        }
        else
        {
            purchAgreementForm.updateDesign_PSN(purchAgreementHeader_ds, ALine_DS);
            purchAgreementHeader_ds.allowEdit(true);
            purchAgreementHeader_ds.allowDelete(true);
        }
    }

    if (purchAgreementHeader.isAgreementCLMOwned())
    {
        element.lockCLMOwnedHeaderFields();
    }

    return ret;
}

/// <summary>
/// Determines whether the current Purhcase Agreement Header is valid to be written.
/// </summary>
/// <returns>
/// true if the record is valid; otherwise, false.
/// </returns>
public boolean validateWrite()
{
    boolean ret;

    // <GEERU>
    if (countryRegion_RU)
    {
        if (purchAgreementHeader.AgreementClassification)
        {
            purchAgreementForm.formMethodDataSourceCreate(purchAgreementHeader_ds);
        }
        else
        {
            return checkFailed(strFmt("@SYS84753", fieldPName(PurchAgreementHeader, AgreementClassification)));
        }
        if (!purchAgreementHeaderExt_RU.AgreementDate)
        {
            return checkFailed(strFmt("@SYS84753", fieldPName(PurchAgreementHeaderExt_RU, AgreementDate)));
        }

        if (!purchAgreementHeaderExt_RU.checkAgreementUniqueness(purchAgreementHeader))
        {
            return false;
        }
    }
    // </GEERU>

    ret = super();

    // Check project information is set correctly...
    if (agreementHeaderDefault_ds.object(fieldNum(AgreementHeaderDefault, Project)).mandatory()
        && !agreementHeaderDefault.Project)
    {
        ret = checkFailed(strFmt("@SYS84753", fieldPName(AgreementHeaderDefault, Project)));
    }

    if (ret && !purchAgreementForm.formMethodDataSourceValidateWrite(purchAgreementHeader_ds))
    {
        ret = false;
    }

    return ret;
}

/// <summary>
/// Committing the Number Sequence record and saving the current default commitment type before writing the
/// Purchase Agreement Header record to the database.
/// Also hanles currency conversion if necessary.
/// </summary>
public void write()
{
    purchAgreementForm.formMethodDataSourceWrite(purchAgreementHeader_ds);

    // Save the default line commitment type
    purchAgreementForm.parmDefaultCommitmentType(purchAgreementHeader.DefaultAgreementLineType);
    purchAgreementForm.saveSysLastValue();

    if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector))
        && PurchAgreementHeader.WorkflowStatus_PSN == PurchAgreementWorkflowStatus::Approved
        && purchAgreementForm.nonStatusHeaderFieldChanged(purchAgreementHeader_ds))
    {
        purchAgreementHeader.WorkflowStatus_PSN = PurchAgreementWorkflowStatus::NotSubmitted;
        purchAgreementHeader.AgreementState     = AgreementState::OnHold;
    }

    super();

    // <GEERU>
    if (countryRegion_RU)
    {
        purchAgreementForm.refreshHeaderDefaultDimension();
        purchAgreementHeaderExt_RU_DS.reread();
        purchAgreementHeaderExt_RU_DS.refresh();
    }
    // </GEERU>
}

/// <summary>
/// Deleting the Purhcase Agreement Header from the database before aborting the Number Sequence record
/// and enabling or disabling the form buttons.
/// </summary>
public void delete()
{
    super();

    if (!purchAgreementHeader.RecId)
    {
        element.setTitleText(true);
    }

    purchAgreementForm.formMethodDataSourceDelete(purchAgreementHeader_ds);
}

/// <summary>
/// Creating a new Purchase Agreement Header record getting the next Agreement ID from the number sequence
/// and enabling or disabling the form buttons.
/// </summary>
/// <param name="_append">
/// Boolean indicating whether the new record should be appended.
/// </param>
public void create(boolean _append = false)
{
    AgreementCreateResult createResult;
    PurchAgreementHeader newAgreementHeader;
    boolean calledFromPurchAgreementForNew;

    calledFromPurchAgreementForNew = element.args().menuItemName() == menuitemDisplayStr(PurchAgreementForNew);

    createResult = purchAgreementForm.create();

    if (!createResult.parmCanceled())
    {
        newAgreementHeader = createResult.parmAgreementHeader();

        if (purchAgreementForm.hasVendorChangedInVendorContext(element.args(), newAgreementHeader))
        {
            purchAgreementForm.openRecordInNewForm(newAgreementHeader);
            if (calledFromPurchAgreementForNew)
            {
                element.close();
            }
        }
        else
        {
            purchAgreementHeader.data(createResult.parmAgreementHeader() as PurchAgreementHeader);
            agreementHeaderDefault.data(createResult.parmAgreementHeaderDefault());
            purchAgreementHeaderDefault.data(createResult.parmPurchSalesAgreementHeaderDefault() as PurchAgreementHeaderDefault);
            purchAgreementHeaderExt_RU.data(createResult.parmAgreementHeaderExt_RU() as PurchAgreementHeaderExt_RU);
            tmsPurchAgreementHeader.data(createResult.parmTmsAgreementHeader() as TMSPurchAgreementHeader);

            // Rebuilding the shared code object with the new datasources.
            element.buildSharedDatasourceManager();
            // Refreshing the form with new datasource data.
            element.args().lookupRecord(newAgreementHeader); // When the details view opens it needs to open the new record
            purchAgreementHeader_ds.rereadJoinHierarchy();
            purchAgreementHeader_ds.research(true);

            if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
            {
                if (purchAgreementHeader.WorkflowStatus_PSN == PurchAgreementWorkflowStatus::Approved)
                {
                    purchAgreementForm.updateWorkflowStatusForEdit_PSN(purchAgreementHeader_ds);
                    element.updateWorkflowControls();
                }

                element.enableButtons();
            }

            tmsPurchAgreementHeader.setCarrierFieldsFromDlvMode(agreementHeaderDefault.DeliveryMode);
            element.enableTMSFields();
            LineViewLines.setFocus();
        }
    }
    else
    {
        // If we don't create a new agreement then we close the main form.
        if (calledFromPurchAgreementForNew)
        {
            element.close();
        }
    }

    #task;
    element.task(#taskSwitchToDetailsView);

    // This line forces a reset of the DEC when a new header is created
    // as on this form simply creating a new header doesn't refresh the
    // child data source consistently which causes the DEC to show
    // previous values.
    if (PurchSalesAgreementDimensionsClearOnCreateFlight::instance().isEnabled())
    {
        DimensionEntryControlLine.loadAttributeValueSet(0);
    }

}

