/// <summary>
/// Determines whether the current Agreement Header is valid to be written.
/// </summary>
/// <returns>
/// true if the record is valid; otherwise, false.
/// </returns>
public boolean validateWrite()
{
    boolean ret;

    if (!agreementHeaderDefault.AgreementHeader)
    {
        if (!purchAgreementHeader.RecId)
        {
            purchAgreementHeader_ds.write();
        }
        agreementHeaderDefault.AgreementHeader = purchAgreementHeader.RecId;
    }

    ret = super();

    ret = ret && purchAgreementFormDatasourceManager.validateProject();

    return ret;
}

