/// <summary>
/// Writes the Agreement Line record to the data base after setting the current
/// Inventory dimensions ID.
/// </summary>
public void write()
{
    boolean rereadHeader;

    inventDim_ds.write();

    aLine.InventDimId           = inventDim.InventDimId;
    aLine.InventDimDataAreaId   = appl.company().dataArea(tableNum(InventDim));

    if ( !aLine.RecId
        || aLine.EffectiveDate != aLine.orig().EffectiveDate
        || aLine.ExpirationDate != aLine.orig().ExpirationDate
        || purchAgreementHeader.mustUpdateWorkflowStatusOnLineChange_PSN())
    {
        //Header will be updated in this case so it should be reread.
        rereadHeader = true;
    }

    super();

    if (!agreementLineDefault.AgreementLine)
    {
        agreementLineDefault.AgreementLine = aLine.RecId;
        agreementLineDefault_ds.write();
    }

    if (rereadHeader)
    {
        purchAgreementHeader_ds.reread();
    }

    if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
    {
        if (purchAgreementHeader.WorkflowStatus_PSN == PurchAgreementWorkflowStatus::Approved)
        {
            purchAgreementForm.updateWorkflowStatusForEdit_PSN(purchAgreementHeader_ds);
        }

        purchAgreementForm.updateDesign_PSN(purchAgreementHeader_ds, ALine_DS);

        element.updateWorkflowControls();
        element.enableButtons();
    }
}

/// <summary>
/// Deletes the current Agreement Line record and sets the state of the line buttons.
/// </summary>
public void delete()
{
    AgreementLine agreementLine;

    super();

    if (PublicSectorUtils::isFrenchRegulatoryEnabled())
    {
        select count(RecId) from agreementLine
           where agreementLine.Agreement == purchAgreementHeader.RecId;

        purchAgreementChild_PSN.enabled(agreementLine.RecId ? false : true);
    }

    // Rereading header datasource due to some header updating on the AgreementLine
    // Insert(), Update() and Delete() methods.
    purchAgreementHeader_ds.reread();
}

/// <summary>
/// Enables or disables the Net Amount field.
/// </summary>
public void enableNetAmount()
{
    netAmount.allowEdit(!aLine.isQuantityBased());
    netAmount.skip(aLine.isQuantityBased());
}

/// <summary>
/// Gets or sets the amount on either the Agreement Line Volume Commitment or the
/// Agreement Line Quantity Commitment record.
/// </summary>
/// <param name="_set">
/// true if the Amount has been modified; otherwise, false.
/// </param>
/// <param name="_agreemetnLine">
/// A table buffer containing the current Agreement Line record.
/// </param>
/// <param name="_amount">
/// Amount that has been entered.
/// </param>
/// <returns>
/// Either the committed amount from the Agreement Line Volume Commitment or the
/// calculated line amount from the Agreement Line Quantity Commitment.
/// </returns>
edit LineAmount editNetAmount(boolean       _set,
                              AgreementLine _agreemetnLine,
                              LineAmount    _amount)
{
    AgreementLineQuantityCommitment agreementLineQuantityCommitment;
    AgreementLineVolumeCommitment   agreementLineVolumeCommitment;
    LineAmount                      lineAmount;

    if (_set)
    {
        if (!_agreemetnLine.isQuantityBased())
        {
            agreementLineVolumeCommitment = _agreemetnLine as AgreementLineVolumeCommitment;
            agreementLineVolumeCommitment.CommitedAmount = _amount;
        }
    }

    if (_agreemetnLine.isQuantityBased())
    {
        agreementLineQuantityCommitment = _agreemetnLine as AgreementLineQuantityCommitment;
        lineAmount = agreementLineQuantityCommitment.lineAmount();
    }
    else
    {
        agreementLineVolumeCommitment = _agreemetnLine as AgreementLineVolumeCommitment;
        lineAmount = agreementLineVolumeCommitment.CommitedAmount;
    }
    return lineAmount;
}

