/// <summary>
/// Enabling or disabling Agreement Line and Inventory Dimensions fields based on the active record and
/// setting the form caption text.
/// </summary>
/// <returns>
/// Result of the super call.
/// </returns>
public int active()
{
    int ret = super();

    // In case of a research and there are no more records the datasource gets activated
    // but there is no valid record and this will throw an excpetion in purchAgreementForm.enableLineFields().
    if (ALine.Agreement)
    {
        var editabilityDataProvider = AgreementLineFieldEditabilityDataProvider::newFromAgreementLine(aLine);
        purchAgreementForm.enableLineFields(aLine_ds,
                                            aLine_agreementLineQuantityCommitment_ds,
                                            editabilityDataProvider);

        purchAgreementForm.enableLineDefaultFields(agreementLineDefault_ds,
                                                   editabilityDataProvider);

        if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
        {
            purchAgreementForm.enableLineButtons(aLine, purchAgreementHeader);
            purchAgreementForm.updateDesign_PSN(purchAgreementHeader_ds, ALine_DS);
            element.updateWorkflowControls();
        }

        this.enableNetAmount();

        inventDim_ds.allowEdit(editabilityDataProvider.canEditInventoryDimensions());

        element.updateDesign(InventDimFormDesignUpdate::Active);
        element.setTitleText();

        // Query Fulfillment remainders.
        element.updateFollowUpFields();

        if (CLMIntegrationFeature::isEnabled() 
            && purchAgreementForm.parmIsIntegrationOwnerCLM())
        {
            element.lockCLMOwnedLineFields();
        }
    }

    return ret;
}

/// <summary>
/// Creating a new Agreement Line record and enabling or disabling fields.
/// </summary>
/// <param name="_append">
/// Boolean indicating whether the new record should be appended.
/// </param>
public void create(boolean _append = true)
{
    super(_append);

    var editabilityDataProvider = AgreementLineFieldEditabilityDataProvider::newFromAgreementLine(aLine);
    editabilityDataProvider.parmIsLineReleased(false);

    purchAgreementForm.enableLineFields(aLine_ds,
                                        aLine_agreementLineQuantityCommitment_ds,
                                        editabilityDataProvider);

    purchAgreementForm.enableLineDefaultFields(agreementLineDefault_ds,
                                               editabilityDataProvider);

    purchAgreementForm.enableLineFieldsItemIdOrCategory(aLine_ds, editabilityDataProvider);

    if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
    {
        purchAgreementForm.enableLineButtons(aLine, purchAgreementHeader);
        purchAgreementForm.updateDesign_PSN(purchAgreementHeader_ds, ALine_DS);

        if (PublicSectorUtils::isFrenchRegulatoryEnabled())
        {
            purchAgreementChild_PSN.enabled(false);
        }
    }

    this.enableNetAmount();

    agreementLine_ItemId.setFocus();
}

