/// <summary>
/// If the Agreement Classification has Direct invoicing checked then show the non-stocked lookup.
/// </summary>
/// <param name="_formControl">
/// This is the default parameter for referece of lookup control
/// </param>
/// <param name="_filterStr">
/// Selected string from the lookup
/// </param>
public void lookup(
    FormControl _formControl,
    str         _filterStr)
{
    InventLookup lookup;

    if (AgreementClassification::find(purchAgreementHeader.AgreementClassification).DirectInvoiceEnable_PSN)
    {
        lookup = InventLookupItemIdNonStocked::newLookupCtrl(_formControl);
        lookup.run();
    }
    else if (EcoResProductSearchLookup::isProductSearchLookupEnabled())
    {
        EcoResProductSearchLookup::lookupProduct(_formControl as FormStringControl);
    }
    else
    {
        InventTable::lookupItem(_formControl);
    }
}

/// <summary>
/// Called when the Agreement Line Item ID is modified.
/// </summary>
/// <remarks>
/// Initializing Agreement Line values from Inventory Table and enabling or disabling fields and setting
/// the form caption text.
/// </remarks>
public void modified()
{
    boolean setDefaultProjectCategory = false;
    super();

    if (productFromLookup) // if there was product selected from the product lookup
    {
        if (inventDim.copyProductDimensions(InventDim::find(productFromLookup.InventDimId),
                                            aLine.ItemId))
        {
            aLine.InventDimId = InventDim::findOrCreate(inventDim).inventDimId;
            aLine.name();
        }
        aLine_ds.cacheCalculateMethod(tableMethodStr(AgreementLine, name));
        productFromLookup = null;
    }

    aLine.itemIdChanged();

    if (aLine.ProjectProjId)
    {
        // If project category is set for the AgreementLine buffer
        // we need to default ProjectCategory for AgreementDefault buffer!
        // EVEN FOR NOT YET SAVED RECORD.
        setDefaultProjectCategory = true;
    }
    agreementLineDefault.initFromInventTable(aLine.inventTable(), setDefaultProjectCategory);

    if (aLine.isQuantityBased())
    {
        aLine_agreementLineQuantityCommitment.initFromInventTable();
        // <GEERU>
        if (countryRegion_RU)
        {
            agreementLineExt_RU.initFromInventTable(aLine_agreementLineQuantityCommitment.inventTable());
            agreementLineExt_RU_ds.refresh();
        }
        // </GEERU>
    }

    element.updateDesign(InventDimFormDesignUpdate::FieldChange);

    purchAgreementForm.enableLineQuantityFieldsDependentOnItem(aLine_agreementLineQuantityCommitment_ds);

    element.setTitleText();

    aLine_ds.refresh();
}

            public boolean validate()
            {
                boolean					   ret;
                smmActivityParentLinkTable smmactivityParentLinkTableLoc;
                ProjTable                  projTable;

                ret = super();

                ret = ret && ALine.validateProject();

                if (agreementLineDefault.ProjectActivityNumber)
                {
                    select firstonly ActivityNumber from smmactivityParentLinkTableLoc
						where smmactivityParentLinkTableLoc.ActivityNumber == agreementLineDefault.ProjectActivityNumber                              
					exists join projTable
                        where projTable.TableId == smmactivityParentLinkTableLoc.RefTableId && 
                              projTable.RecId == smmactivityParentLinkTableLoc.RefRecId &&
                              projTable.ProjId == ALine.ProjectProjId;

                    if (!smmactivityParentLinkTableLoc.ActivityNumber)
                    {
                        agreementLineDefault.ProjectActivityNumber = '';
                        error (strFmt("@SYS133442"));
                    }
                }

                return ret;
            }

