public boolean validateWrite()
{
    boolean ret = true;

    if (countryRegion_RU)
    {
        if (!purchAgreementHeaderExt_RU.AgreementHeader)
        {
            ret = purchAgreementHeaderExt_RU.checkVatOperationCode(purchAgreementHeader.VendAccount);

            if (ret)
            {
                if (!purchAgreementHeader.RecId)
                {
                    purchAgreementHeader_ds.write();
                }
                purchAgreementHeaderExt_RU.initFromAgreementHeader(purchAgreementHeader);
            }
        }

        ret = ret && super();
    }

    return ret;
}

public void write()
{
    boolean doRefreshDim = false;

    if (countryRegion_RU)
    {
        doRefreshDim = purchAgreementHeaderExt_RU.RecId == 0;

        super();

        if (doRefreshDim)
        {
            purchAgreementHeader_ds.reread();
            purchAgreementHeader_ds.refresh();
        }
    }
}

