    PurchAgreementSubcontractor paSubcontractor = PurchAgreementSubcontractor::findSubContractor(purchAgreementCertification.VendTable, purchAgreementHeader.RecId);

    isValidVendor = (paSubcontractor.RecId || purchAgreementHeader.VendAccount == purchAgreementCertification.VendTable) && super();

    return isValidVendor;
}

