    {
        tmsPurchAgreementHeader.initFromPurchAgreement(purchAgreementHeader.RecId);
    }

    super();

    tmsPurchAgreementHeader.ModeCode  = TMSCarrier::find(tmsPurchAgreementHeader.CarrierCode).ModeCode;

    if (!tmsPurchAgreementHeader.CarrierCode
    || !TMSCarrierService::exist(tmsPurchAgreementHeader.CarrierServiceCode, tmsPurchAgreementHeader.CarrierCode))
    {
        tmsPurchAgreementHeader.CarrierServiceCode    = "";
    }

    tmsPurchAgreementHeader_ds.refresh();

    element.tmsUpdatePurchAgreement();

    element.enableTMSFields();
}

