public void pageActivated()
{
    super();

    if (!isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
    {
        // Now we need to query fulfillment data
        // and assign counters to correspondend controls to show.
        element.updateFollowUpFields();
    }
}

public void clicked()
{
    super();

    BudgetResLineAgreementLine_PSN budgetResLineAgreementLine;

    BudgetReservationLineSelectorContract_PSN budgetReservationLineSelectorContract
        = BudgetReservationLineSelectorContract_PSN::newFromParameters(BudgetReservationRelievingDocument_PSN::PurchaseRequisition);
    budgetReservationLineSelectorContract.parmIsPurchaseAgreementLine(true);

    boolean budgetReservationsExist = false;

    while select BudgetReservationLine from budgetResLineAgreementLine
        where budgetResLineAgreementLine.AgreementLine == ALine.RecId
    {
        budgetReservationsExist = true;

        budgetReservationLineSelectorContract.addSelectedBudgetReservationLine(budgetResLineAgreementLine.BudgetReservationLine);
    }

    if (budgetReservationsExist)
    {
        RecordInsertList reservationAgreementLineInsertList = new RecordInsertList(budgetResLineAgreementLine.TableId);

        AgreementLine agreementLineInsert;

        while select RecId from agreementLineInsert
            where agreementLineInsert.Agreement == ALine.Agreement
                && agreementLineInsert.RecId != ALine.RecId
        {
            budgetReservationLineSelectorContract.initializeEnumerator();

            BudgetReservationLineRecId_PSN budgetReservationLineRecId = budgetReservationLineSelectorContract.getNextBudgetReservationLineRecId();

            while (budgetReservationLineRecId)
            {
                if (!BudgetResLineAgreementLine_PSN::existsByBudgetResLineAgreementLine(budgetReservationLineRecId, agreementLineInsert.RecId))
                {
                    budgetResLineAgreementLine.BudgetReservationLine = budgetReservationLineRecId;
                    budgetResLineAgreementLine.AgreementLine = agreementLineInsert.RecId;

                    reservationAgreementLineInsertList.add(budgetResLineAgreementLine);
                }

                budgetReservationLineRecId = budgetReservationLineSelectorContract.getNextBudgetReservationLineRecId();
            }
        }

        if (reservationAgreementLineInsertList.insertDatabase())
        {
            info("@PublicSector:PurchAgreementGBRCopySuccess");
        }
        else
        {
            info("@PublicSector:PurchAgreementGBRCopyAlreadyAdded");
        }
    }
    else
    {
        info("@PublicSector:PurchAgreementGBRCopyNoReferences");
    }
}

public void clicked()
{
    super();

    if (Box::yesNo("@PublicSector:PurchAgreementGBRDeleteConfirmBox", DialogButton::No) == DialogButton::Yes)
    {
        #OCCRetryCount;

        try
        {
            ttsBegin;

            MultiSelectionHelper selectedAgreementLines = MultiSelectionHelper::construct();
            selectedAgreementLines.parmDatasource(ALine_ds);

            AgreementLine agreementLineSelection = selectedAgreementLines.getFirst();

            while (agreementLineSelection)
            {
                BudgetResLineAgreementLine_PSN budgetResLineAgreementLine;

                delete_from budgetResLineAgreementLine
                    where budgetResLineAgreementLine.AgreementLine == agreementLineSelection.RecId;

                agreementLineSelection = selectedAgreementLines.getNext();
            }

            ttsCommit;

            info("@PublicSector:PurchAgreementGBRDeleteSuccess");
        }
        catch (Exception::Deadlock)
        {
            if (xSession::currentRetryCount() >= #RetryNum)
            {
                throw Exception::Deadlock;
            }
            else
            {
                retry;
            }
        }
        catch (Exception::Error)
        {
            throw Exception::Error;
        }
    }
}

