public void lookup()
{
    if (PublicSectorUtils::isFrenchRegulatoryEnabled())
    {
        if (purchAgreementHeaderDefault.VendorInvoiceAccount == '')
        {
            VendBankAccount::lookupBankAccount(this,
                                                purchAgreementHeader.VendAccount,
                                                CompanyInfo::findRecId(CompanyInfo::current()).DataArea,
                                                DateTimeUtil::getSystemDateTime());
        }
        else
        {
            VendBankAccount::lookupBankAccount(this,
                                                purchAgreementHeaderDefault.VendorInvoiceAccount,
                                                CompanyInfo::findRecId(CompanyInfo::current()).DataArea,
                                                DateTimeUtil::getSystemDateTime());
        }
    }
}

public void lookup()
{
    purchAgreementForm.lookupMiscChargeGroup(this, MarkupModuleType::Vend);
}

public void jumpRef()
{
    Args args = new Args();
    args.caller(element);
    args.lookupRecord(MarkupGroup::find(MarkupModuleType::Vend, agreementHeaderDefault.MiscChargeGroup));

    new MenuFunction(menuItemDisplayStr(MarkupGroup_Vend), MenuItemType::Display).run(args);
}

public void lookup()
{
    TMSCarrierService::lookupCarrierServiceCode(this, tmsPurchAgreementHeader.CarrierCode);
}

public boolean modified()
{
    boolean ret;

    if (tmsPurchAgreementHeader.RecId == 0)
    {
        tmsPurchAgreementHeader.initFromPurchAgreement(purchAgreementHeader.RecId);
    }

    ret = super();

    element.enableTMSFields();

    return ret;
}

